pragma solidity 0.5.7;
pragma experimental ABIEncoderV2;

import "./interfaces/ViaCash.sol";
import "./interfaces/ViaBond.sol";
import "./interfaces/ViaToken.sol";
import "./interfaces/ViaOracle.sol";
import "./interfaces/ViaFactory.sol";
import "./interfaces/VerifiedClient.sol";
import "./interfaces/VerifiedRates.sol";

import "./math/Math.sol";
import "./math/FixedPoint.sol";

import "./utilities/ERC20.sol";
import "./utilities/Pausable.sol";
import "./utilities/StringUtils.sol";
import "./utilities/BokkyPooBahsDateTimeLibrary.sol";

import "@openzeppelin/upgrades/contracts/Initializable.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/ownership/Ownable.sol";

contract Bond is ViaBond, Initializable, ERC20, Ownable, Pausable {

    using stringutils for *;
    using BokkyPooBahsDateTimeLibrary for uint;
    using Math for uint256;
    using FixedPoint for uint256;

    //token factory address
    ViaFactory private factory;

    VerifiedRates private rates;

    //oracle
    ViaOracle private oracle;

    //name of Via token (eg, Via-USD)
    bytes32 public bondName;
    
    //token address
    address private token;

    //mapping issuer (address) to address of bond token (address) which acts as identifier for bonds on offer
    mapping(address => mapping (address => ViaBond.bond)) public issues;

    //mapping purchaser (address) to address of bond token (address) which acts as identifier for bonds subscribed
    mapping(address => mapping (address => ViaBond.bond)) public purchases;

    //array of issues of this bond, where bond IDs are their addresses of their token issues
    address[] public bondsIssued;

    //events to capture and report to Via oracle
    event BondIssued(address indexed party, address token, bytes32 bondName, uint256 amount, bytes32 currency, uint256 collateralAmount, uint256 issueTime);
    event BondRedeemed(address indexed party, address token, uint256 tokenAmount, bytes32 tokenName, uint256 amount, bytes32 currency);
    event BondPurchased(address indexed party, address token, bytes32 bondName, uint256 amount, bytes32 currency, uint256 paidInAmount, uint256 purchaseTime);
    event BondLiquidated(address indexed party, address token, uint256 tokenAmount, bytes32 tokenName, uint256 amount, bytes32 currency);

    //mutex
    //mapping(bytes32 => bool) private lock;
    bool private lock;

    //initiliaze proxies
    function initialize(bytes32 _name, bytes32 _currency, address _owner, address _oracle, address _token, address _fee) public initializer {
        Ownable.initialize(_owner);
        factory = ViaFactory(msg.sender);
        oracle = ViaOracle(_oracle);
        setName(string(abi.encodePacked(_name)));
        setSymbol(_name.substring(0,5));
        bondName = _currency;
        token = _token;
        rates = VerifiedRates(_fee);
    }

    //handling pay in of ether for issue of bond tokens
    function() external payable{
        //ether paid in
        require(msg.value !=0);
        //only to pay in ether
        require(msg.data.length==0);
        // contract must not be paused
        require(paused == false);
        //check aml status
        require(amlCheck(msg.sender)==true); 
        //issue bond tokens
        issue(msg.value, msg.sender, "ether", address(this));
    }

    function requestIssue(uint256 amount, address payer, bytes32 currency, address cashContract) external returns(bool){
        // contract must not be paused
        require(paused == false);
        //bonds denominated in a currency can not be collateralized with that currency itself
        require(bondName!=currency);
        //check aml status
        require(amlCheck(payer)==true);
        require(factory.getType(msg.sender) == "ViaCash");
        return(issue(amount, payer, currency, cashContract));
    }

    //requesting issue of bond tokens to payer (issuer) that can pay in ether, or in cash tokens
    function issue(uint256 amount, address payer, bytes32 currency, address cashContract) private returns(bool){
        //ensure that brought amount is not zero
        require(amount != 0);        
        //adds paid in amount to the paid in currency's cash balance
        if(currency!="ether"){
            //non ether currency (or cash tokens) can be paid in to issue bonds
            if(factory.getType(cashContract)=="ViaCash"){
                if(!ViaCash(cashContract).requestAddToBalance(amount, payer)){
                    return false;
                }
            }
            else{
                return false;
            }
        }
        //pay issuing fee
        amount = payFee(amount, currency, "issuing");
        //call oracle to calculate par value
        uint256 parValue;
        if(factory.getTokenByNameType(currency, "ViaCash")!=address(0x0))
            //if the cash token paid in to a bond token is different from the denomination of this bond, 
            //tokens of this bond need to be issued from the issuers' account after pricing the bond with applicable coupon rates
            parValue = oracle.request(currency, bondName, "ver");
        else
            //if ether or any other supported token is paid into a bond contract, 
            //the bond contract will issue bond tokens of an equivalent face value.
            parValue = oracle.request(currency, bondName.substringinbytes(3,5), "er");
        //request issue of bond at par value
        finallyIssue(payer, parValue, amount, currency);
    }

    //issue bond tokens using factory and set total supply of issued bond token
    function finallyIssue(address payer, uint256 parValue, uint256 paidInAmount, bytes32 paidCurrency) private {
        uint256 issueTime = now;
        //issue bond which initializes a token with the attributes of the bond
        address issuedBond = factory.createToken(token, payer, bondName, keccak256(abi.encodePacked(payer,issueTime)));
        //initialize bond token
        ViaToken vt = ViaToken(issuedBond);
        vt.addBalance(payer, parValue);
        vt.addTotalSupply(parValue);        
        //add issued bond balance
        _mint(payer, parValue);
        //keep track of issues
        bondsIssued.push(issuedBond);
        storeIssuedBond(payer, parValue, paidInAmount, paidCurrency, issueTime, issuedBond);
        //generate event
        emit BondIssued(payer, issuedBond, bondName, parValue, paidCurrency, paidInAmount, issueTime);
    }

    //stores issued bond details
    function storeIssuedBond(address _party, uint256 _parValue, uint256 _paidInAmount, bytes32 _paidInCurrency, uint256 _timeIssued, address _issuedBond) private {
        bond storage b = issues[_party][_issuedBond];
        b.counterParties.push(_party);
        b.parValue = _parValue;
        b.paidInAmount = _paidInAmount;
        b.paidInCurrency = _paidInCurrency;
        b.timeIssuedOrSubscribed = _timeIssued;
    }

    function requestPurchase(uint256 amount, address payer, bytes32 currency, address cashContract) external returns(bool){
        // contract must not be paused
        require(paused == false);
        //check aml status
        require(amlCheck(payer)==true);
        require(factory.getType(msg.sender) == "ViaBondToken");
        require(factory.getType(cashContract)=="ViaCash");
        return(purchase(amount, payer, currency, cashContract, msg.sender));
    }

    //requesting purchase of bond tokens to payer (buyer) that can pay in cash tokens
    function purchase(uint256 amount, address payer, bytes32 currency, address cashContract, address tokenContract) private returns(bool){
        //ensure that brought amount is not zero
        require(amount != 0);
        //adds paid in amount to the paid in currency's cash balance
        if(!ViaCash(cashContract).requestAddToBalance(amount, payer)){
            return false;
        }
        uint256 er;
        uint256 ir;
        uint256 priced;
        if(currency!=bondName){
            //if the cash token paid in to a bond token is different from the denomination of this bond, 
            //tokens of this bond need to be transferred from an issuers' account after pricing the bond with applicable coupon rates
            er = oracle.request(currency, bondName, "er");
            ir = oracle.request(currency, bondName, "ir");
            priced = FixedPoint.divDown(FixedPoint.mulDown(amount, er), FixedPoint.add(1e18, ir));
        }
        else{
            //if the cash token paid in is the same denomination of this bond, then
            //tokens of this bond need to be transferred from an issuer's account after pricing the bond with the domestic (paid in currency) coupon rates
            ir = oracle.request(currency, currency, "ir");
            priced = FixedPoint.divDown(amount, FixedPoint.add(1e18, ir));
        }
        //request purchase of bond
        finallyPurchase(payer, priced, amount, currency, tokenContract);
    }

    function finallyPurchase(address payer, uint256 purchaseValue, uint256 paidInAmount, bytes32 paidInCashToken, address tokenContract) private {
        //paid in amount is Via cash with which bond tokens are purchased
        ViaToken vt = ViaToken(tokenContract);
        address issuer = vt.getIssuer();
        require(!lock);
        lock = true;
        //transfer cash paid in by purchaser to issuer from whom bond is transferred to purchaser
        address viaAddress = factory.getIssuer("ViaCash", paidInCashToken);
        if(viaAddress!=address(0x0)){
            //if there is enough issued bonds, transfer bond from issuer to payer
            if(ViaToken(tokenContract).transferToken(issuer, payer, purchaseValue)){                
                _transfer(issuer, payer, purchaseValue);
                //deduct paid out cash token from purchaser cash balance which was added to cash contract's balance and transfer to issuer
                sendCash(viaAddress, paidInAmount, issuer);
                //add purchaser as counter party in issuer's record
                bond storage b = issues[issuer][tokenContract];
                b.counterParties.push(payer);
                //add to issued value of bond for amount transferred by purchaser
                issues[issuer][tokenContract].purchasedIssueAmount = Math.add(issues[issuer][tokenContract].purchasedIssueAmount, purchaseValue); 
                //add bond to purchaser's record
                uint256 purchaseTime = now;
                storePurchasedBond(payer, issuer, purchaseValue, paidInAmount, paidInCashToken, purchaseTime, tokenContract);
                //bond purchased event
                emit BondPurchased(payer, tokenContract, bondName, purchaseValue, paidInCashToken, paidInAmount, purchaseTime);
            }
        }
        lock = false;        
    }

    //stores purchased bond details
    function storePurchasedBond(address _payer, address _party, uint256 _purchasedIssueAmount, uint256 _paidInAmount, bytes32 _paidInCashToken, uint256 _timeSubscribed, address _issuedBond) private {
        bond storage b = purchases[_payer][_issuedBond];
        b.counterParties.push(_party);
        b.purchasedIssueAmount = _purchasedIssueAmount;
        b.paidInAmount = _paidInAmount;
        b.paidInCurrency = _paidInCashToken;
        b.timeIssuedOrSubscribed = _timeSubscribed;
    }

    function requestRedemption(uint256 amount, address payer, bytes32 currency, address tokenContract) external returns(bool){
        // contract must not be paused
        require(paused == false);
        //check aml status
        require(amlCheck(payer)==true);
        require(factory.getType(msg.sender) == "ViaBondToken");
        //if the paying in is for repayment of a bond already issued, then
        //transfer the paid in amount to the bond holders, release the collateral back to the issuer and extinguish the bond
        if(redeem(amount, payer, currency, "CashTransfer", tokenContract))
            return true;
        else
            return false;
    }
    
    //requesting redemption of Verified bonds and transfer of ether or cash collateral to issuer 
    function redeem(uint256 amount, address payer, bytes32 tokenName, bytes32 action, address tokenContract) private returns(bool){
        //if Via bond holder redeems bond on day of expiry, issuer collateral is transferred to bond holder
        if(action=="TokenTransfer"){
            bool status = false;
            //find if the bond was issued to payer (purchaser) earlier
            if(purchases[payer][tokenContract].purchasedIssueAmount==amount){
                //check if redemption term is over
                if(now >= BokkyPooBahsDateTimeLibrary.addSeconds(purchases[payer][tokenContract].timeIssuedOrSubscribed, factory.getBondTerm())){
                    uint256 redemptionAmount = FixedPoint.mulDown(FixedPoint.divDown(amount, issues[purchases[payer][tokenContract].counterParties[0]][tokenContract].parValue), 
                                                issues[purchases[payer][tokenContract].counterParties[0]][tokenContract].paidInAmount);
                    require(!lock);
                    lock = true;                    
                    if(redemptionAmount >= 0){
                        bytes32 paidInCurrency = issues[purchases[payer][tokenContract].counterParties[0]][tokenContract].paidInCurrency;
                        address viaAddress = factory.getIssuer("ViaCash", paidInCurrency);
                        //pay fee on bond redemption
                        redemptionAmount = payFee(redemptionAmount, paidInCurrency, "redemption");
                        //transfer ether collateral from issuer to purchaser (redeemer) of bond
                        if(paidInCurrency=="ether")
                            sendEther(payer, redemptionAmount);
                        else
                            sendCash(viaAddress, redemptionAmount, payer);
                        //adjust total supply of this bond
                        ViaToken(tokenContract).reduceSupply(amount);                        
                        //reduce payer's balance of bond held
                        ViaToken(tokenContract).reduceBalance(payer, amount);
                        _burn(payer, amount);
                        //generate event
                        emit BondLiquidated(payer, tokenContract, amount, tokenName, redemptionAmount, paidInCurrency);
                        //remove payer (purchaser) from counter party list of issuer 
                        address issuer = purchases[payer][tokenContract].counterParties[0]; 
                        for(uint p=0; p<issues[issuer][tokenContract].counterParties.length; p++){
                            if(issues[issuer][tokenContract].counterParties[p]==payer){
                                delete(issues[issuer][tokenContract].counterParties[p]);
                                //reduce issuer's par value by amount redeemed
                                //issues[issuer][tokenContract].parValue = Math.sub(issues[issuer][tokenContract].parValue, FixedPoint.mulDown(FixedPoint.divDown(
                                //    issues[issuer][tokenContract].parValue, issues[issuer][tokenContract].purchasedIssueAmount), amount));
                            }                    
                        }
                        //remove payer (purchaser) record from list of purchasers
                        delete(purchases[payer][tokenContract]);
                    }
                    lock = false;
                    status = true;
                }
            }
            //find if the bond was issued to payer (issuer) earlier and the issuer is returning unsold bond tokens
            else if(Math.sub(issues[payer][tokenContract].parValue,  issues[payer][tokenContract].purchasedIssueAmount) >= amount){
                require(!lock);
                lock = true;
                bytes32 paidInCurrency = issues[payer][tokenContract].paidInCurrency;
                address viaAddress = factory.getIssuer("ViaCash", paidInCurrency);
                //calculate redemption amount based on how much collateral is not encumbered
                uint256 uncumberedAmount = FixedPoint.mulDown(FixedPoint.divDown(amount, issues[payer][tokenContract].parValue), issues[payer][tokenContract].paidInAmount);
                //pay fee on bond redemption
                uncumberedAmount = payFee(uncumberedAmount, paidInCurrency, "redemption");
                //transfer uncumbered ether from issuer to issuer (redeemer) of bond
                if(paidInCurrency=="ether")
                    sendEther(payer, uncumberedAmount);
                else{
                    sendCash(viaAddress, uncumberedAmount, payer);
                }
                //reduce issuer's par value by amount redeemed
                //issues[payer][tokenContract].parValue = Math.sub(issues[payer][tokenContract].parValue, FixedPoint.mulDown(FixedPoint.divDown(
                //    issues[payer][tokenContract].parValue, issues[payer][tokenContract].purchasedIssueAmount), amount));
                //adjust total supply of this bond
                ViaToken(tokenContract).reduceSupply(amount);                
                //reduce payer's balance of bond held
                ViaToken(tokenContract).reduceBalance(payer, amount);
                _burn(payer, amount);
                //generate event
                emit BondRedeemed(payer, tokenContract, amount, tokenName, uncumberedAmount, paidInCurrency);
                if(issues[payer][tokenContract].parValue >= amount){
                    //if bond is redeemed in full by issuer, then remove issue from list of issues
                    delete(issues[payer][tokenContract]);
                }
                lock = false; 
                status = true;
            }
            return status;
        }
        //if Via bond issuer pays in cash for redemption, it is paid back to Via bond holders and collateral paid back to issuers
        else if(action=="CashTransfer"){
            address viaAddress;
            bool status = false;
            uint256 totalToRedeem;
            if(now >= BokkyPooBahsDateTimeLibrary.addSeconds(issues[payer][tokenContract].timeIssuedOrSubscribed, factory.getBondTerm())){
                viaAddress = factory.getIssuer("ViaCash", tokenName);
                //find the bond that was issued by payer (issuer) earlier
                if(amount == issues[payer][tokenContract].parValue){
                    uint p = 0;
                    for(p=0; p<issues[payer][tokenContract].counterParties.length; p++){
                        address purchaser = issues[payer][tokenContract].counterParties[p];
                        //release collateral and transfer ether to issuer of bond
                        //and send paid in amount to purchaser of bond
                        //calculate redemption amount
                        uint256 proportionPurchased = FixedPoint.divDown(purchases[purchaser][tokenContract].purchasedIssueAmount, issues[payer][tokenContract].parValue);
                        uint256 redemptionAmount = FixedPoint.mulDown(proportionPurchased, issues[payer][tokenContract].parValue);
                        require(!lock);
                        lock = true;
                        //send paid in amount to bond purchaser
                        if(viaAddress!=address(0x0) && (redemptionAmount >= 0)){
                            //pay fee on bond redemption
                            redemptionAmount = payFee(redemptionAmount, tokenName, "redemption");
                            sendCash(viaAddress, redemptionAmount, purchaser);
                            //add redeemed amount to total
                            totalToRedeem = Math.add(totalToRedeem, redemptionAmount);
                            //adjust total supply of this bond
                            ViaToken(tokenContract).reduceSupply(redemptionAmount);                            
                            //reduce counter party's balance of bond held
                            ViaToken(tokenContract).reduceBalance(purchaser, redemptionAmount);
                            _burn(purchaser, redemptionAmount);
                            status = true;
                        }
                        lock = false;
                        if(status)
                            //if a purchaser is redeemed, then delete its record from list of purchases
                            delete(purchases[purchaser][tokenContract]);                    
                    }
                    if(status){
                        //find proportion to redeem
                        uint256 proportionToRedeem = FixedPoint.divDown(totalToRedeem, issues[payer][tokenContract].purchasedIssueAmount);
                        //returned redeemed proportion of collateral to payer (issuer)
                        uint256 amountToRedeem = FixedPoint.mulDown(issues[payer][tokenContract].paidInAmount, proportionToRedeem);
                        bytes32 paidInCurrency = issues[payer][tokenContract].paidInCurrency;
                        if(paidInCurrency=="ether")
                            //send redeemed ether to payer
                            sendEther(payer, amountToRedeem);
                        else
                            sendCash(viaAddress, amountToRedeem, payer);
                        if(p==issues[payer][tokenContract].counterParties.length-1)
                            //if all bond purchasers are redeemed, then delete the issuer record
                            delete(issues[payer][tokenContract]);
                        uint256 _amount = amount;
                        emit BondRedeemed(payer, tokenContract, _amount, tokenName, amountToRedeem, paidInCurrency);
                    }
                }
            }
            return status;
        }
    }
    
    //redirected from transferFrom function in bond token contract
    function transferForward(address sender, address receiver, uint256 tokens, address forwarder) external returns (bool){
        // contract must not be paused
        require(paused == false);
        //check aml status
        require(amlCheck(sender)==true);
        require(amlCheck(receiver)==true);     
        //check if tokens are being transferred to this bond contract
        if(receiver != address(this) && receiver == forwarder){         
            //if token name is the same, this transfer has to be redeemed
            if(redeem(tokens, sender, bondName, "TokenTransfer", forwarder))
                return true;
            else
                return false;
        }
        else {
            //bond tokens are being sent to a user account
            //sender is transferring its full purchase amount of bonds
            //transfer of bond token to another user has to be of the exact amount purchased earlier by sender
            if(purchases[sender][forwarder].purchasedIssueAmount==tokens){
                if(transferBondToken(forwarder, sender, receiver, tokens))
                    return true;
                else
                    return false;                
            }
            return false;
        }
    }

    function transferBondToken(address issuedBond, address sender, address receiver, uint tokens) private returns (bool){
        _transfer(sender, receiver, tokens);
        if(ViaToken(issuedBond).transferToken(sender, receiver, tokens)){
            purchases[receiver][issuedBond] = purchases[sender][issuedBond];
            delete purchases[sender][issuedBond];
            return true;
        }
        else{
            //reversing transfer if bond tokens fail to transfer
            _transfer(receiver, sender, tokens);
            return false;
        }
    }

    function getBonds() external view returns(address[] memory){
        return bondsIssued;
    }

    function getBondIssues(address issuer, address bondToken) external view returns(uint256, uint256, uint256, bytes32, uint256){
        return (issues[issuer][bondToken].parValue,
                issues[issuer][bondToken].purchasedIssueAmount,
                issues[issuer][bondToken].paidInAmount,
                issues[issuer][bondToken].paidInCurrency,
                issues[issuer][bondToken].timeIssuedOrSubscribed);
    }

    function getBondPurchases(address purchaser, address bondToken) external view returns(uint256, uint256, bytes32, uint256){
        return (issues[purchaser][bondToken].purchasedIssueAmount,
                issues[purchaser][bondToken].paidInAmount,
                issues[purchaser][bondToken].paidInCurrency,
                issues[purchaser][bondToken].timeIssuedOrSubscribed);
    }

    function sendCash(address cash, uint256 amount, address payee) private {
        ViaCash(cash).requestDeductFromBalance(amount, payee);
    }

    function sendEther(address payee, uint256 amount) private {
        address(uint160(payee)).transfer(amount); 
    }

    function payFee(uint256 value, bytes32 currency, bytes32 action) private returns (uint256){
        uint256 fees;
        uint256 returnValue;
        if(action=="issuing")
            fees = rates.getFee("issuing");
        else if(action=="redemption")
            fees = rates.getFee("redemption");
        if(fees > 0){
            address feeTo = rates.getCustodian();
            address cashContract = factory.getTokenByNameType(currency, "ViaCash");
            if(feeTo!=address(0x0)){
                if(currency=="ether")
                    address(uint160(feeTo)).transfer(FixedPoint.mulDown(fees, value));
                else
                    ERC20(cashContract).transfer(feeTo, FixedPoint.mulDown(fees, value));
                returnValue = Math.sub(value, FixedPoint.mulDown(fees, value));
                return returnValue;
            }
        }
        return value;
    }

    //check AML status for account address
    function amlCheck(address account) private view returns(bool){
        address client = factory.getClient();
        if(client==address(0x0))
            return true;
        if(VerifiedClient(client).getAMLStatus(account))
            return true;
        else
            return false;
    }
    
    function pause() onlyFactoryOwner(msg.sender, address(factory)) public {
        _pause();
    }
    function unpause() onlyFactoryOwner(msg.sender, address(factory)) public {
        _unpause();
    }

    modifier onlyFactoryOwner (address _sender, address _factory){
        require(_sender == owner() || _sender == _factory);
        _;
    }
    
}
