pragma solidity 0.5.7;

import "./interfaces/ViaCash.sol";
import "./interfaces/ViaBond.sol";
import "./interfaces/ViaToken.sol";
import "./interfaces/ViaOracle.sol";
import "./interfaces/ViaFactory.sol";
import "./interfaces/VerifiedClient.sol";
import "./interfaces/VerifiedRates.sol";

import "./math/Math.sol";
import "./math/FixedPoint.sol";

import "./utilities/Pausable.sol";
import "./utilities/StringUtils.sol";
import "./utilities/ERC20.sol";

import "@openzeppelin/upgrades/contracts/Initializable.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/ownership/Ownable.sol";

contract Cash is ViaCash, Initializable, ERC20, Ownable, Pausable {

    using stringutils for *;
    using Math for uint256;
    using FixedPoint for uint256;

    //via factory address
    ViaFactory private _factory;

    VerifiedRates private _rates;

    //via oracle
    ViaOracle private _oracle;

    //name of Via cash token (eg, Via-USD)
    bytes32 public _cashtokenName;

    struct deposit{
        uint256 depositAmount;
        uint256 issuedAmount;
        address depositor;
    }

    bytes32[] private currencies;

    mapping(bytes32 => uint256) private currencyIndex;

    //mapping of buyers (address) to deposit currency (bytes32) to deposits 
    mapping(address => mapping(bytes32 => deposit)) public _deposits;

    modifier onlyCustodian(address sender){
        //address client = _factory.getClient();
        //(bytes32 role, , ) = VerifiedClient(client).getRole(sender);
        //require(role=="Custodian" || role=="Admin");
        _;
    }

    //events to capture and report to Via oracle
    event CashIssued(address indexed party, bytes32 currency, uint256 amount, uint256 balance);
    event CashRedeemed(address indexed party, bytes32 currency, uint256 amount, uint256 redeemedFor, uint256 balance);
    event CashDeposits(address indexed party, bytes32 currency, uint256 amount);
    event CashTransfer(address indexed party, address indexed counterparty, bytes32 currency, uint256 amount, uint256 deposit);

    //mutex
    mapping(bytes32 => bool) private _lock;

    //initiliaze proxies
    function initialize(bytes32 name, bytes32 currency, address owner, address oracle, address token, address fee) public initializer{
        Ownable.initialize(owner);        
        _factory = ViaFactory(msg.sender);
        _oracle = ViaOracle(oracle);
        setName(string(abi.encodePacked(name)));
        setSymbol(name.substring(0,5));
        _cashtokenName = currency;
        _rates = VerifiedRates(fee);
    }

    //handling pay in of ether for issue of via cash tokens
    function() external payable {
        //ether paid in
        require(msg.value !=0);
        //only to pay in ether
        require(msg.data.length==0);
        //contract must not be paused
        require(paused == false);
        //check aml status
        require(amlCheck(msg.sender)==true);        
        //keep paid in currency for reference
        if(currencyIndex["ether"]==0){
            currencies.push("ether");
            currencyIndex["ether"] = currencies.length;
        }
        //issue via cash tokens
        makeDeposit(msg.value, msg.sender, "ether");        
    }

    //accessor for issue function
    function requestIssue(uint256 amount, address buyer, bytes32 currency) public {
        require(_factory.getType(msg.sender) == "ViaCash" || _factory.checkSupportForToken(currency)==true);
        bool status = true;
        if(_factory.checkSupportForToken(currency)==true){
            //for ERC20 tokens supported by Verified such as DAI, USDC, etc
            uint256 scalingFactor = _computeScalingFactor(IERC20(_factory.getCurrencyToken(currency)));
            amount = _upscale(amount, scalingFactor);
            address token = _factory.getCurrencyToken(currency);
            IERC20(token).transferFrom(msg.sender, address(this), amount);
        }
        else {
            address cashToken = _factory.getTokenByNameType(currency,"ViaCash");
            //transfer amount of currency from party to the deposit currency's cash contract 
            if(cashToken!=address(this))
                status = Cash(address(uint160(cashToken))).requestAddToBalance(amount, buyer);
            else
                status = addToBalance(amount, buyer);
        }
        if(status){
            //keep paid in currency for reference
            if(currencyIndex[currency]==0){
                currencies.push(currency);
                currencyIndex[currency] = currencies.length;
            }
            makeDeposit(amount, buyer, currency);
        }
    }

    //requesting issue of Via to buyer for amount of ether or some other supported cash token paid in and stored in cashContract
    function makeDeposit(uint256 amount, address buyer, bytes32 currency) private {
        //ensure that brought amount is not zero
        require(amount != 0);
        require(!_lock["receipt"], "Lock on deposits");
        _lock["receipt"] = true;
        _deposits[buyer][currency].depositAmount = Math.add(_deposits[buyer][currency].depositAmount, amount);
        _deposits[buyer][currency].depositor = buyer;
        // request oracle for exchange value of crypto paid in
        uint256 issueAmount;
        if(_factory.getTokenByNameType(currency, "ViaCash")!=address(0x0))
            //exchange of one verified cash token to another
            issueAmount = _oracle.request(currency, _cashtokenName, "ver");
        else
            //exchange of paid in supported token (not a verified cash token) to the fiat currency corresponding to this verified cash token
            issueAmount = _oracle.request(currency, _cashtokenName.substringinbytes(2,5), "er");
        emit CashDeposits(buyer, currency, amount); 
        issue(buyer, currency, issueAmount);
        _lock["receipt"] = false;     
    }

    //handles fiat pay in for issue of cash tokens by authorized custodians on Verified Network
    function payIn(uint256 amount, address payer, bytes32 currency) onlyCustodian(msg.sender) external returns(bool){
        //contract must not be paused
        require(paused == false);
        //keep paid in currency for reference
        if(currencyIndex[currency]==0){
            currencies.push(currency);
            currencyIndex[currency] = currencies.length;
        }
        _deposits[payer][currency].depositor = msg.sender;
        _deposits[payer][currency].depositAmount = Math.add(_deposits[payer][currency].depositAmount, amount);
        //scale up paid in amount, just in case decimals are incorrectly sent by app
        issue(payer, currency, FixedPoint.mulDown(amount, 1e18));
    }

    //party is the user account address to which issued tokens are credited 
    //currency is what is paid in
    //amount is quantity of this verified cash token to issue
    function issue(address party, bytes32 currency, uint256 amount) private {
        //add paid in currency to depositor
        _deposits[party][currency].issuedAmount = Math.add(_deposits[party][currency].issuedAmount, amount);
        //add issued amount to payer
        _mint(party, amount);
        //generate event        
        emit CashIssued(party, _cashtokenName, amount, balanceOf(party));
    }

    //accessor for addToBalance function
    function requestAddToBalance(uint256 tokens, address sender) external returns (bool){
        require(_factory.getType(msg.sender) == "ViaCash" || _factory.getType(msg.sender) == "ViaBond");
        return addToBalance(tokens, sender);
    }

    //add to token balance of this contract from token balance of sender
    function addToBalance(uint256 tokens, address sender) private returns (bool){
        //sender should have more tokens than being transferred
        if(balanceOf(sender) >= tokens){
            //transferFrom(sender, address(this), tokens);
            _transfer(sender, address(this), tokens);
            return true;
        }
        else
            return false;
    }
    
    //requesting redemption of Verified cash token and transfer of currency it was issued against
    //operation parameter indicates whether it is a redemption or a transfer of deposits from one party to another
    function redeem(uint256 amount, address party, bytes32 operation, address receiver) private returns(bool){
        bool found = false;
        for(uint i=0; i<currencies.length && amount>0; i++){            
            //check if currency in which redemption is to be done has sufficient issued balance for the deposited currency
            if(_deposits[party][currencies[i]].issuedAmount >= amount){
                require(!_lock["redeem"], "Lock on redemption");
                _lock["redeem"] = true;
                uint256 value = FixedPoint.mulDown(FixedPoint.divDown(amount, _deposits[party][currencies[i]].issuedAmount), _deposits[party][currencies[i]].depositAmount);
                _deposits[party][currencies[i]].depositAmount = Math.sub(_deposits[party][currencies[i]].depositAmount, value);                
                _deposits[party][currencies[i]].issuedAmount = Math.sub(_deposits[party][currencies[i]].issuedAmount, amount);
                if(operation=="redeem"){
                    (amount, value) = payFee(party, amount, value, currencies[i], "withdrawl");
                    //reduces balances
                    _burn(party, amount);
                    //send redeemed ether to party
                    if(currencies[i]=="ether"){
                        address(uint160(party)).transfer(value);
                    }
                    else if(_factory.checkSupportForToken(currencies[i])==true){
                        //non verified cash ERC20 token
                        address token = _factory.getCurrencyToken(currencies[i]);
                        IERC20(token).transfer(party, value);
                    }
                    else if(_factory.getTokenByNameType(currencies[i], "ViaCash")!=address(0x0)){   
                        //verified cash token                
                        address cashToken = _factory.getTokenByNameType(currencies[i],"ViaCash");
                        if(cashToken==address(this))
                            deductFromBalance(value, party);
                        else
                            Cash(address(uint160(cashToken))).requestDeductFromBalance(value, party);
                    }
                    else if(currencyIndex[currencies[i]]!=0){
                        //if currency to redeem is fiat counterpart of this cash token, request oracle to pay out fiat
                        _oracle.payOut(_deposits[party][currencies[i]].depositor, party, currencies[i], value);
                    }
                    //generate event
                    emit CashRedeemed(party, currencies[i], value, amount, balanceOf(party));
                }
                else if(operation=="transfer"){
                    //check if any fee is payable on remittance and acceptance and pay it if that is the case
                    (amount, value) = payFee(party, amount, value, currencies[i], "transfer");
                    //reduces balances
                    _burn(party, amount);
                    //transfer balances
                    _mint(receiver, amount);
                    //transfer deposits
                    _deposits[receiver][currencies[i]].depositAmount = Math.add(_deposits[receiver][currencies[i]].depositAmount, value);
                    //transfer issued amounts
                    _deposits[receiver][currencies[i]].issuedAmount = Math.add(_deposits[receiver][currencies[i]].issuedAmount, amount);                    
                    //change depositor 
                    _deposits[receiver][currencies[i]].depositor = _deposits[party][currencies[i]].depositor;
                    emit CashTransfer(party, receiver, currencies[i], amount, value);
                }
                amount = 0;
                found = true;
                _lock["redeem"] = false;
            }
            //amount to redeem is more than what is in deposit, so we need to remove deposit after redemption
            else if(_deposits[party][currencies[i]].issuedAmount > 0){                
                require(!_lock["redeem"], "Lock on redemption");
                _lock["redeem"] = true;
                uint256 value;
                if(operation=="redeem"){    
                    //check if any fee is payable on redemption and pay it if that is the case
                    (amount, value) = payFee(party, _deposits[party][currencies[i]].issuedAmount, _deposits[party][currencies[i]].depositAmount, currencies[i], "withdrawl");          
                    //reduces balances
                    _burn(party, amount);      
                    //send redeemed ether to party which is all of the ether in deposit with this user (party)
                    if(currencies[i]=="ether"){    
                        address(uint160(party)).transfer(value);
                    }
                    else if(_factory.checkSupportForToken(currencies[i])==true){
                        //non verified cash token
                        address token = _factory.getCurrencyToken(currencies[i]);
                        IERC20(token).transfer(party, value);
                    }
                    else if(_factory.getTokenByNameType(currencies[i], "ViaCash")!=address(0x0)){  
                        //verified cash token                
                        address cashToken = _factory.getTokenByNameType(currencies[i],"ViaCash");
                        if(cashToken==address(this))
                            deductFromBalance(value, party);
                        else
                            Cash(address(uint160(cashToken))).requestDeductFromBalance(value, party);
                    }     
                    else if(currencyIndex[currencies[i]]!=0){
                        //if currency to redeem is fiat counterpart of this cash token, request oracle to pay out fiat
                        _oracle.payOut(_deposits[party][currencies[i]].depositor, party, currencies[i], value);
                    }               
                    //generate event
                    emit CashRedeemed(party, currencies[i], value, _deposits[party][currencies[i]].issuedAmount, balanceOf(party));
                }
                else if(operation=="transfer"){
                    //check if any fee is payable on remittance and acceptance and pay it if that is the case
                    (amount, value) = payFee(party, _deposits[party][currencies[i]].issuedAmount, _deposits[party][currencies[i]].depositAmount, currencies[i], "transfer");
                    //reduces balances
                    _burn(party, amount);
                    //transfer balances
                    _mint(receiver, amount);
                    //transfer deposits
                    _deposits[receiver][currencies[i]].depositAmount = Math.add(_deposits[receiver][currencies[i]].depositAmount, value);
                    //transfer issued amounts
                    _deposits[receiver][currencies[i]].issuedAmount = Math.add(_deposits[receiver][currencies[i]].issuedAmount, amount);
                    //change depositor 
                    _deposits[receiver][currencies[i]].depositor = _deposits[party][currencies[i]].depositor;
                    emit CashTransfer(party, receiver, currencies[i], amount, value);
                }
                //calculate remaining amount to redeem or transfer
                amount = Math.sub(amount, _deposits[party][currencies[i]].issuedAmount);
                //make deposit and issued amount balances zero for ether
                delete _deposits[party][currencies[i]];
                found = true;
                _lock["redeem"] = false;
            }
        }
        return found;
    }

    function burnCashTokens(uint256 amount, address party, bytes32 currency) onlyCustodian(msg.sender) external returns(bool){
        uint256 value = FixedPoint.mulDown(FixedPoint.divDown(amount, _deposits[party][currency].issuedAmount), _deposits[party][currency].depositAmount);
        _deposits[party][currency].depositAmount = Math.sub(_deposits[party][currency].depositAmount, value);
        //reduces balances
        _burn(party, amount);
        _deposits[party][currency].issuedAmount = Math.sub(_deposits[party][currency].issuedAmount, amount);
    } 

    //accessor for deductFromBalance function
    function requestDeductFromBalance(uint256 tokens, address receiver) external returns (uint256){
        require(_factory.getType(msg.sender) == "ViaCash" || _factory.getType(msg.sender) == "ViaBond");
        return(deductFromBalance(tokens, receiver));
    }

    //deduct token balance from this contract and add token balance to receiver
    function deductFromBalance(uint256 tokens, address receiver) private returns (uint256){
        //this cash token issuer should have more tokens than being deducted
        if(balanceOf(address(this)) >= tokens){
            _transfer(address(this), receiver, tokens);
            return 0;
        }
        else{
            uint256 balance = Math.sub(tokens, balanceOf(address(this)));
            _transfer(address(this), receiver, balanceOf(address(this)));
            return balance;
        }
    }
    
    //overriding this function of ERC20 standard for transfer of via cash tokens to other users or to this contract for redemption
    function transferFrom(address sender, address receiver, uint256 tokens) public returns (bool){
        //contract must not be paused
        require(paused == false);
        //check aml status
        require(amlCheck(sender)==true);
        require(amlCheck(receiver)==true);
        //ensure sender has enough tokens in balance before transferring or redeeming them
        require(balanceOf(sender) >= tokens, "Insufficient balance for transfer");
        //check if tokens are being transferred to this cash contract
        if(receiver == address(this)){
            //if token name is the same, this transfer has to be redeemed
            if(redeem(tokens, sender, "redeem", address(this)))
                return true;
            else
                return false;
        }
        //else request issue of cash tokens requested from receiver
        else if(_factory.getType(receiver)=="ViaCash"){ 
            require(!_lock["transfer"], "Lock on transfer");
            _lock["transfer"] = true;
            //only issue if cash tokens are paid in, since bond tokens can't be paid to issue bond token
            Cash(address(uint160(receiver))).requestIssue(tokens, sender, _cashtokenName);
            _lock["transfer"] = false; 
            return true;                
        }
        //else if cash tokens are paid into bond issuers, then request for issue of bonds
        else if(_factory.getType(receiver)=="ViaBond"){
            if(ViaBond(address(uint160(receiver))).requestIssue(tokens, sender, _cashtokenName, address(this)))
                    return true;
                else
                    return false;
        }
        //else check if cash tokens are paid into any specific bond token for purchase
        else if(_factory.getType(receiver)=="ViaBondToken"){
            if(ViaToken(address(uint160(receiver))).requestTransaction(tokens, sender, _cashtokenName, address(this)))
                    return true;
                else
                    return false;
        }
        else{
            //else, tokens are being sent to another user's account
            //sending contract should be allowed by token owner to make this transfer
            if(redeem(tokens, sender, "transfer", receiver))
                return true;
            else
                return false;
        } 
    }

    //amount is issued, value is deposits against issues    
    function payFee(address payer, uint256 amount, uint256 value, bytes32 currency, bytes32 action) private returns (uint256, uint256){
        uint256 fees;
        if(action=="transfer")
            fees = _rates.getFee("remittance");
        else if(action=="withdrawl")
            fees = _rates.getFee("withdrawl");
        if(fees > 0){
            address feeTo = _rates.getCustodian();
            if(feeTo!=address(0x0)){
                amount = Math.sub(amount, FixedPoint.mulDown(fees, amount));
                value = Math.sub(value, FixedPoint.mulDown(fees, value));
                _transfer(payer, feeTo, FixedPoint.mulDown(fees, amount));
                _deposits[feeTo][currency].issuedAmount = Math.add(_deposits[feeTo][currency].issuedAmount, FixedPoint.mulDown(fees, amount));
                _deposits[feeTo][currency].depositAmount = Math.add(_deposits[feeTo][currency].depositAmount, FixedPoint.mulDown(fees, value)); 
            }
        }
        return (amount, value);
    }

    //check AML status for account address
    function amlCheck(address account) private view returns(bool){
        address client = _factory.getClient();
        if(client==address(0x0))
            return true;
        if(VerifiedClient(client).getAMLStatus(account))
            return true;
        else
            return false;
    }

    function pause() onlyFactoryOwner(msg.sender) public {
        _pause();
    }
    
    function unpause() onlyFactoryOwner(msg.sender) public {
        _unpause();
    }

    modifier onlyFactoryOwner (address sender){
        require(sender == owner() || sender == address(_factory));
        _;
    }

    function _computeScalingFactor(IERC20 token) internal view returns (uint256) {

        // Tokens that don't implement the `decimals` method are not supported.
        uint256 tokenDecimals = ERC20(address(token)).decimals();

        // Tokens with more than 18 decimals are not supported.
        uint256 decimalsDifference = Math.sub(18, tokenDecimals);
        return 1e18 * 10**decimalsDifference;
    }

    function _upscale(uint256 amount, uint256 scalingFactor) internal pure returns (uint256) {
        
        return FixedPoint.mulDown(amount, scalingFactor);
    }

    function _downscaleDown(uint256 amount, uint256 scalingFactor) internal pure returns (uint256) {
        return FixedPoint.divDown(amount, scalingFactor);
    }

    function _downscaleUp(uint256 amount, uint256 scalingFactor) internal pure returns (uint256) {
        return FixedPoint.divUp(amount, scalingFactor);
    }

}
