// Distributions and Voting on corporate actions for Verified securities
// (c) Kallol Borah, 2023

pragma solidity 0.5.7;

import "./Snapshot.sol";

import "@openzeppelin/contracts-ethereum-package/contracts/utils/Arrays.sol";

contract CorporateActions is Snapshot {

    using Arrays for uint256[];

    //mapping snapshot time to security token holder to vote cast
    mapping(uint256 => mapping(address => bool)) internal votes;

    struct Distribution {
        address token;
        uint256 amount;
    }

    //mapping snapshot time to security token holder to payout made
    mapping(uint256 => mapping(address => Distribution)) internal distributions;

    //mapping snapshot time to urls of resolutions
    mapping(uint256 => string) internal resolutions;

    //mapping resolution to indicate whether it is up for voting or not
    mapping(string => bool) internal voting;

    event Resolution(address indexed security, uint256 recordDate, string resolution, bool voting);

    //checks if time to create resolution for is present in current snapshots and if yes, record the resolution
    function _createResolution(uint256 time, bool vote, string memory ipfsLink) internal{
        uint256[] memory scheduledSnapshots = getNextSnapshots();
        for(uint256 i=0; i<scheduledSnapshots.length; i++){
            if(scheduledSnapshots[i]==time){
                resolutions[time] = ipfsLink;
                voting[ipfsLink] = vote;
                emit Resolution(address(this), time, ipfsLink, vote);
            }
        }
    }

    //fetches resolution for given time
    function getResolution(uint256 time) public view returns(string memory, bool){
        if(keccak256(abi.encodePacked(resolutions[time])) != keccak256(abi.encodePacked(""))){
            return (resolutions[time], voting[resolutions[time]]);
        }
    }

    //voting by holders of this security token
    function vote(uint256 time, bool ballot) public {
        require(snapshotBalanceOf(time, msg.sender)>0, "Unauthorized voter");
        if(keccak256(abi.encodePacked(resolutions[time])) != keccak256(abi.encodePacked(""))){
            if(voting[resolutions[time]]==true){
                if(votes[time][msg.sender]==false){
                    votes[time][msg.sender] = ballot;
                }
            }
        }
    }      

    //returns token address and amount paid out to msg.sender (investor) if it has positive balance of this security token at time
    function getDistribution(uint256 time) public view returns(address, uint256){
        if(snapshotBalanceOf(time, msg.sender)>0){//, "No security token balance for sender");
            if(keccak256(abi.encodePacked(resolutions[time])) != keccak256(abi.encodePacked(""))){
                return (distributions[time][msg.sender].token, distributions[time][msg.sender].amount);
            }
        }
    }

}
