// Verified Liquidity Manager 
// (c) Kallol Borah, 2021

//"SPDX-License-Identifier: UNLICENSED"

pragma solidity 0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

import "@openzeppelin/contracts/utils/math/SafeMath.sol";

import "./interfaces/IMarketMaker.sol";
import "./interfaces/ILiquidity.sol";
import "./interfaces/IVitta.sol";

contract Liquidity is Ownable, Pausable {

    using SafeMath for uint256;

    ILiquidity.platform[] private platforms;
    mapping(address => uint256) private platformIndex;

    ILiquidity.token[] private tokens;
    mapping(address => uint256) private tokenIndex;
    
    ILiquidity.provider[] private LPs;

    uint256 investmentCap=0;
    uint256 timeLimit=0;
    
    uint256 totalStaked=0;
    uint256 totalLiquidity=0;
    uint256 totalIssued=0;

    address private vitta;

    struct investment{
        uint256 tokenAmount;
        address tokenAddress;
    }

    struct liquidity{
        uint256 issued;
        uint256 staked;
    }

    struct performance{
        uint256 liquidityProvided;
        uint256 commissionsEarned;
    }

    // mapping investor to invested tokens
    mapping(address => mapping(string => investment)) private investors;
    // mapping investor to liquidity tokens issued and staked
    mapping(address => liquidity) private stakes;    
    // mapping platform to asset managers on it
    mapping(address => address[]) private managers; 
    // mapping platform to asset manager's position in it
    mapping(address => mapping(address => uint256)) private managerIndex;
    // mapping platform to address of token distributed by manager to asset manager index to performance of liquidity provided
    mapping(address => mapping(address => mapping(uint256 => performance))) private managerRoi;
    // mapping platform to performance
    mapping(address => performance) private platformRoi;

    //mutex
    mapping(bytes32 => bool) private lock;

    event RequestIssue(address investor, string tokenName, uint256 tokenAmount);
    
    /**
        Constructor for the Verified Liquidity manager
        @param  _cap    investment limit for each investor
        @param  _limit  time limit for issue close
        @param  _vitta  address of Verified liquidity token
     */
    constructor(uint256 _cap, uint256 _limit, address _vitta){        
        investmentCap = _cap;
        timeLimit = _limit;
        totalIssued = 0;
        vitta = _vitta;        
    }

    /**
        Updates investment and time limits for Verified Liquidity token after initial issue with constructor defined parameters
        @param  _cap    investment limit for each investor
        @param  _limit  time limit for issue close 
     */
    function createSupply(uint256 _cap, uint256 _limit) onlyOwner external {
        investmentCap = _cap;
        timeLimit = _limit;
    }

    /**
        Specifies list of supported tokens that can be invested in the Verified Liquidity token
        @param  _token  address of token supported
        @param  _name   name of token supported
     */
    function supportTokens(address _token, bytes32 _name) onlyOwner external {
        if(tokenIndex[_token]==0){
            ILiquidity.token memory t = ILiquidity.token ({
                tokenName: _name,
                tokenAddress: _token
            });
            tokens.push(t);
            tokenIndex[_token] = tokens.length;
        }
    }

    /**
        Checks if a specified token is supported for investing in the Verified Liquidity token
        @param  _token  token that is supported for investment
     */
    function checkSupportForToken(address _token) external view returns(bool){
        if(tokenIndex[_token]>0)
            return true;
        else
            return false;
    }

    /**
        Returns list of supported tokens
     */
    function getSupportedTokens() external view returns(ILiquidity.token[] memory){
        return tokens;
    }

    /**
        Registers a liquidity platform (eg, DEX) where Verified Liquidity tokens can be used to underwrite investments
        @param  _liquidityPlatform  address of the market making platform
        @param  _platformName       name of market making platform
     */
    function registerPlatform(address _liquidityPlatform, bytes32 _platformName) onlyOwner external {
        if(platformIndex[_liquidityPlatform]==0){
            ILiquidity.platform memory p = ILiquidity.platform({
                platformName: _platformName,
                platformAddress: _liquidityPlatform
            });
            platforms.push(p);
            platformIndex[_liquidityPlatform] = platforms.length;
        }
    }
    
    /**
        Gets list of registered platforms
     */
    function getPlatforms() external view returns(ILiquidity.platform[] memory){
        return platforms;
    }

    /**
        Adds asset manager for managing liquidity
        @param  _platform   address of market making platform (eg, Balancer)
        @param  _manager    address of asset manager
     */
    function addManager(address _platform, address _manager) onlyOwner external{
        if(managerIndex[_platform][_manager]==0){
            managers[_platform].push(_manager);
            managerIndex[_platform][_manager] = managers[_platform].length;
        }
    } 

    /**
        Removes asset manager for managing liquidity on platform
        @param  _platform   address of market making platform
        @param  _manager    address of asset manager
     */
    function removeManager(address _platform, address _manager) onlyOwner external{
        bool found = false;
        if(managerIndex[_platform][_manager]!=0){
            for(uint i=0; i<managers[_platform].length; i++){
                if(managers[_platform][i]==_manager){
                    if(i==managers[_platform].length-1){
                       delete managers[_platform][i];
                       managerIndex[_platform][_manager] = 0;
                    }
                    else{
                       managers[_platform][i]=managers[_platform][i+1];
                       managerIndex[_platform][managers[_platform][i]] = managerIndex[_platform][managers[_platform][i]] - 1;
                    }
                    found = true;
                }
                else if(found){
                    if(i==managers[_platform].length-1){
                       delete managers[_platform][i];
                       managerIndex[_platform][_manager] = 0;
                    }
                    else{
                       managers[_platform][i]=managers[_platform][i+1];
                       managerIndex[_platform][managers[_platform][i]] = managerIndex[_platform][managers[_platform][i]] - 1;
                    }
                }
            }
        }
    }

    /**
        Gets asset managers for managing liquidity
        @param  _platform   address of market making platform
     */
    function getManagers(address _platform) onlyOwner external view returns(address[] memory){
        return managers[_platform];
    }

    /**
        Receiver of ether invested in the Verified Liquidity token
     */
    receive() external payable{
        if(timeLimit!=0)
            require(timeLimit > block.timestamp, "Subscription window breached");
        //ether paid in
        require(msg.value !=0, "Zero eth sent");
        //contract must not be paused
        require(paused() == false, "Contract in paused state");
        //block reentrants
        require(!lock["etherreceipt"]);
        lock["etherreceipt"] = true;
        //issue liquidity token        
        if(investors[msg.sender]["ether"].tokenAmount==0){
            ILiquidity.provider memory lp = ILiquidity.provider({
                assetInvested: string("ether"),
                investorAddress: msg.sender
            });
            LPs.push(lp);
        }
        investors[msg.sender]["ether"].tokenAmount = SafeMath.add(investors[msg.sender]["ether"].tokenAmount, msg.value);
        emit RequestIssue(msg.sender, "ether", msg.value);
        lock["etherreceipt"] = false;
    }

    /**
        Used by external apps (eg, exchange, wallet) to buy Verified Liquidity token 
        @param  _token  address of token used by investor to buy the VITTA
        @param  _amount amount of token that is transferred from investor to this VITTA issuing contract's account for the investor
     */
    function buy(address _token, uint256 _amount) external {
        if(timeLimit!=0)
            require(timeLimit > block.timestamp, "Subscription window breached");
        require(paused() == false, "Contract in paused state");
        bool found = false;
        if(tokenIndex[_token]>0)
            found = true;
        if(found){
            require(!lock["buy"]);
            lock["buy"] = true;
            if(IERC20(_token).transferFrom(msg.sender, address(this), _amount)){
                emit RequestIssue(msg.sender, ERC20(_token).name(), _amount);
                if(investors[msg.sender][ERC20(_token).name()].tokenAmount==0){
                    ILiquidity.provider memory lp = ILiquidity.provider({
                        assetInvested: ERC20(_token).name(),
                        investorAddress: msg.sender
                    });
                    LPs.push(lp);
                }
                investors[msg.sender][ERC20(_token).name()].tokenAmount = SafeMath.add(investors[msg.sender][ERC20(_token).name()].tokenAmount, _amount);
                investors[msg.sender][ERC20(_token).name()].tokenAddress = _token;
            }
            lock["buy"] = false;
        }
    }

    /**
        Fetches investors in VITTA
     */
    function getInvestors() onlyOwner external view returns(ILiquidity.provider[] memory){
        return LPs;
    }

    /**
        Fetches investment detail for specific investor in VITTA
        @param  _investor   address of investor
        @param  _tokenName  name of token invested by investor
     */
    function getInvestment(address _investor, string memory _tokenName) onlyOwner external view returns(uint256){
        return (investors[_investor][_tokenName].tokenAmount);
    }
    
    /**
        Used by admin to issue VITTA to investors or refund paid in tokens to investors if investment cap is breached
        @param  _investor       address of investor
        @param  _tokenName      name of token invested in VITTA
        @param  _tokenAmount    amount of token invested in VITTA
        @param  _LPToIssue      amount of VITTA to issue to investor 
     */
    function issue(address _investor, string memory _tokenName, uint256 _tokenAmount, uint256 _LPToIssue) onlyOwner external {
        require(!lock["issue"]);
        lock["issue"] = true;
        //If investment is outside limits, refund tokens invested back to investor
        if((_LPToIssue > investmentCap) || (_LPToIssue > SafeMath.sub(IERC20(vitta).totalSupply(), totalIssued))){
            investors[_investor][_tokenName].tokenAmount = SafeMath.sub(investors[_investor][_tokenName].tokenAmount, _tokenAmount);
            if(keccak256(abi.encodePacked(_tokenName))!="ether")
                IERC20(investors[_investor][_tokenName].tokenAddress).transfer(_investor, _tokenAmount);
            else
                payable(_investor).transfer(_tokenAmount);
        }
        //else issue VITTA to investor and transfer managed liquidity to deployer that allocates it to issuer
        else{
            IERC20(vitta).transferFrom(msg.sender, _investor, _LPToIssue);  
            totalIssued = SafeMath.add(totalIssued, _LPToIssue);    
            stakes[_investor].issued = SafeMath.add(stakes[_investor].issued, _LPToIssue);
            totalStaked = SafeMath.add(totalStaked, _LPToIssue); //added for model without staking
            stakes[_investor].staked = SafeMath.add(stakes[_investor].staked, _LPToIssue);//added for model without staking            
        }
        lock["issue"] = false;
    }

    /**
        Provides staked VITTA to asset managers based on prior liquidity consumed and commission earned 
        @param  _platform   address of platform on which liquidity has to be provided
        @param  _manager    address of asset manager to whom liquidity has to be provided
        @param  _liquidity  amount of VITTA tokens to allot to an asset manager
        @param  _token      address of token allotted to asset manager
        @param  _tokenAmount    amount of token allotted to asset manager
     */    
    function provideLiquidity(address _platform, address _manager, uint256 _liquidity, address _token, uint256 _tokenAmount) onlyOwner external {
        require(!lock["liquidity"]);
        lock["liquidity"] = true;
        require(paused() == false, "Contract in paused state");
        require(managerIndex[_platform][_manager]!=0, "Manager does not exist");
        if(tokens[tokenIndex[_token]-1].tokenName!="ether")
            IERC20(_token).transfer(_manager, _tokenAmount);
        else
            payable(_manager).transfer(_tokenAmount);
        IMarketMaker(_platform).stake(_liquidity, _manager);
        totalLiquidity = SafeMath.add(totalLiquidity, _liquidity);
        platformRoi[_platform].liquidityProvided = SafeMath.add(platformRoi[_platform].liquidityProvided, _liquidity);
        uint256 index = managerIndex[_platform][_manager];
        managerRoi[_platform][address(this)][index].liquidityProvided = SafeMath.add(managerRoi[_platform][address(this)][index].liquidityProvided, _liquidity);
        lock["liquidity"] = false;
    }

    /**
        Pay out of income by Market maker contracts to VITTA Liquidity providers
        @param  _distribution   amount of VITTA to mint and distribute pro rata to liquidity providers
        @param  _token          address of token in which distribution is made by market maker    
        @param  _manager        address of asset manager earning the paid out return on investment 
     */
    function distribute(uint256 _distribution, address _token, address _manager) external {
        require(platformIndex[msg.sender]>0);
        require(tokenIndex[_token]>0);
        require(paused() == false, "Contract in paused state");
        require(!lock["distribute"]);
        lock["distribute"] = true;
        uint256 index = managerIndex[msg.sender][_manager];
        if(totalStaked!=0 && index!=0){
            managerRoi[msg.sender][_token][index].commissionsEarned = SafeMath.add(managerRoi[msg.sender][_token][index].commissionsEarned, _distribution);
        }
        lock["distribute"] = false;
    }

    /**
        Fetches performance of market platform platform
        @param  _platform   address of market making platform
     */
    function getPlatformPerformance(address _platform) onlyOwner external view returns(uint256 unstakedLiquidity, 
                                                                                        uint256 balancePlatformLiquidity,
                                                                                        uint256 platformLiquidityProvided,
                                                                                        uint256 platformCommissionsEarned){
        uint256 unStakedLiquidity = SafeMath.sub(totalStaked, totalLiquidity);
        uint256 liquidityBalance = SafeMath.sub(platformRoi[_platform].liquidityProvided, IERC20(vitta).balanceOf(_platform));
        return (unStakedLiquidity, liquidityBalance, platformRoi[_platform].liquidityProvided, platformRoi[_platform].commissionsEarned);
    }

    /**
        Fetches performance of asset managers
        @param  _platform   address of platform
        @param  _token      address of token
        @param  _manager    address of asset manager on platform
     */
    function getManagerPerformance(address _platform, address _token, address _manager) onlyOwner external view returns(uint256 managerLiquidityProvided,
                                                                                    uint256 managerCommissionsEarned){
        uint256 index = managerIndex[_platform][_manager];                                                                                
        return (managerRoi[_platform][_token][index].liquidityProvided, managerRoi[_platform][_token][index].commissionsEarned);
    }

    /**
        Pay out income to VITTA Liquidity providers
        @param  _platform       amount of VITTA to mint and distribute pro rata to liquidity providers
        @param  _distribution   amount of VITTA token to pay out    
     */
    function payOut(uint256 _distribution, address _platform) onlyOwner external {
        require(!lock["payout"]);
        lock["payout"] = true;
        IVitta(vitta).mint(address(this), _distribution);
        platformRoi[_platform].commissionsEarned = SafeMath.add(platformRoi[_platform].commissionsEarned, _distribution);
        for(uint i=0; i<LPs.length; i++){
            uint256 prorataStake = SafeMath.div(stakes[LPs[i].investorAddress].staked, totalStaked);
            IERC20(vitta).transfer(LPs[i].investorAddress, SafeMath.mul(prorataStake, _distribution));                
        }
        lock["payout"] = false;
    }

    function balance(address investor) external view returns(uint256 investorBalance){
        return IERC20(vitta).balanceOf(investor);
    }

    /**
        Pauses this contract
     */
    function pause() onlyOwner public {
        _pause();
    }
    
    /**
        Unpauses this contract
     */
    function unpause() onlyOwner public {
        _unpause();
    }

}

