pragma solidity 0.7.1;
pragma experimental ABIEncoderV2;

import "./interfaces/IFactory.sol";
import "./interfaces/ISecurity.sol";
import "./interfaces/IMarginOrder.sol";
import "./interfaces/IMarginManager.sol";
import "./interfaces/IMarginTradingPool.sol";
import "./interfaces/IMarginTradingPoolFactory.sol";

import '@balancer-labs/v2-vault/contracts/interfaces/IAsset.sol';
import '@balancer-labs/v2-vault/contracts/interfaces/IVault.sol';

import "@balancer-labs/v2-solidity-utils/contracts/math/Math.sol";
import "@balancer-labs/v2-solidity-utils/contracts/math/FixedPoint.sol";
import "@balancer-labs/v2-solidity-utils/contracts/openzeppelin/ERC20.sol";
import "@balancer-labs/v2-solidity-utils/contracts/openzeppelin/IERC20.sol";
import "@balancer-labs/v2-solidity-utils/contracts/openzeppelin/Ownable.sol";

contract MarginIssueManager is IMarginManager, Ownable {

    using Math for *;

    // balancer variables
    address private _poolFactory;    

    address private _vault;

    address private _distribution;

    IFactory private _products;

    uint256 private constant _INITIAL_BPT_SUPPLY = 2**(112) - 1;
    uint256 private constant _DEFAULT_MINIMUM_BPT = 1e6;

    //mapping security address to currency address to pool address
    mapping(address => mapping(address => address)) private pools;

    //mapping pool id to security token traded
    mapping(bytes32 => address) private poolSecurity;

    //mapping pool id to issuer
    mapping(bytes32 => address) private issuer;

    //mapping token address to issuing fee collected
    mapping(address => uint256) private fees;

    //positions mapping trader address to collateral address to positive/negative balance (true for +ve, false for -ve) amount
    mapping(address => mapping(address => int256)) private collateralAmount;

    //traders
    address[] private traders;
    mapping(address => uint256) private traderIndex;

    //positions mapping security to currency to array of trade positions
    mapping(address => mapping(address => IMarginManager.tradePosition[])) private positions;
    //mapping order ref to index of position in positions array
    mapping(bytes32 => uint256) private orders;

    event tradeSettled( 
                        bytes32 indexed poolid,
                        address transferor,
                        address transferee,
                        uint256 unitsToTransfer,
                        address security,
                        uint256 price,
                        address currency,
                        uint256 amountPaid,
                        bytes32 tradeRef,
                        uint256 fee
                    );

    event subscribers(address party, address counterparty, address indexed securityTraded, uint256 securityAmount, 
                        address currencySettled, uint256 cashAmount);

    event closures(bytes32 indexed poolId, address security, uint256 timestamp);
    event feecollection(address indexed platform, uint256 collection, uint256 timestamp);

    modifier onlyIssueManager(address sender, address security){
        address DP = _products.getDP(security);
        require(DP==sender, "Unauthorized access");
        _;
    }

    modifier onlyTrader(address sender){
        require(traderIndex[sender]!=0, "Unauthorized access");
        _;
    }

    function initialize(address poolFactory, address vault, address products, address distribution) public onlyOwner {
        _poolFactory = poolFactory;
        _vault = vault;
        _products = IFactory(products);
        _distribution = distribution;
    }

    function issueProduct(address security, bytes32 securityType, address currency, bytes32 cficode, uint256 securityAmount, uint256 minimumOrderSize, uint256 cashAmount,
                                uint256 margin, uint256 collateral, uint256 tradeFee) override external onlyIssueManager(msg.sender, security) {
        require(IERC20(security).balanceOf(msg.sender)>=securityAmount && securityAmount>=minimumOrderSize
                && IERC20(currency).balanceOf(msg.sender)>=cashAmount, "No balance to create pool");
        ERC20(currency).transferFrom(msg.sender, address(this), cashAmount);
        ERC20(security).transferFrom(msg.sender, address(this), securityAmount);  
        ISecurity(security).whiteList(_vault, securityAmount);      
        IMarginTradingPoolFactory.FactoryPoolParams memory poolparams = IMarginTradingPoolFactory.FactoryPoolParams({
            name : "Verified Pool Token",
            symbol : "VPT",
            security : security,
            securityType: securityType,
            currency : currency,
            cficode: cficode,
            minOrderSize : minimumOrderSize,
            margin : margin,
            collateral: collateral,
            tradeFeePercentage : tradeFee
        });    
        createPool(poolparams, security, securityAmount, currency, cashAmount);        
    }

    function createPool(IMarginTradingPoolFactory.FactoryPoolParams memory poolparams, address security, uint256 securityAmount, address currency, uint256 cashAmount) private {
        pools[security][currency] = IMarginTradingPoolFactory(_poolFactory).create(poolparams);   
        bytes32 poolId = IMarginTradingPool(pools[security][currency]).getPoolId();  
        poolSecurity[poolId] = security; 
        issuer[poolId] = msg.sender;
        joinPool(security, currency, securityAmount, cashAmount, poolId);
    }

    function joinPool(address security, address currency, uint256 securityAmount, uint256 cashAmount, bytes32 poolId) private {
        // join the pool here                
        (IERC20[] memory tokens, , ) = IVault(_vault).getPoolTokens(poolId);
        IAsset[] memory assets = _convertERC20sToAssets(tokens);
        uint256[] memory maxAmountsIn = new uint256[](tokens.length);
        ERC20(currency).approve(_vault, cashAmount);
        ERC20(security).approve(_vault, securityAmount);
        for(uint256 t; t < tokens.length; t++) {
            if(tokens[t]==IERC20(security))
                maxAmountsIn[t] = securityAmount;
            else if(tokens[t]==IERC20(currency))
                maxAmountsIn[t] = cashAmount;
            else 
                maxAmountsIn[t] = Math.sub(_INITIAL_BPT_SUPPLY, _DEFAULT_MINIMUM_BPT);
        }
        IVault.JoinPoolRequest memory request = IVault.JoinPoolRequest({
            assets: assets,
            maxAmountsIn: maxAmountsIn,
            userData: abi.encode(JoinKind.INIT, maxAmountsIn),
            fromInternalBalance: false
        });         
        IVault(_vault).joinPool(poolId, address(this), address(this), request);
    }

    function close(bytes32 poolId) override external onlyIssueManager(msg.sender, poolSecurity[poolId]) {
        address currency = IMarginTradingPool(poolSecurity[poolId]).getCurrency(); 
        // exit the pool here   
        (IERC20[] memory tokens, , ) = IVault(_vault).getPoolTokens(poolId);
        IAsset[] memory assets = _convertERC20sToAssets(tokens);
        uint256[] memory amountsOut = new uint256[](tokens.length);
        IVault.ExitPoolRequest memory request = IVault.ExitPoolRequest({
            assets: assets,
            minAmountsOut: amountsOut,
            userData: abi.encode(ExitKind.EXACT_BPT_IN_FOR_TOKENS_OUT, _INITIAL_BPT_SUPPLY - _DEFAULT_MINIMUM_BPT),
            toInternalBalance: false
        });         
        IVault(_vault).exitPool(poolId, address(this), payable(address(this)), request);
        emit closures(poolId, poolSecurity[poolId], block.timestamp);
        IERC20(currency).transfer(issuer[poolId], IERC20(currency).balanceOf(address(this)));
        IERC20(poolSecurity[poolId]).transfer(issuer[poolId], IERC20(poolSecurity[poolId]).balanceOf(address(this)));
    }

    function offerCollateral(address cashToken, uint256 amount) override external {
        IERC20(cashToken).transferFrom(msg.sender, address(this), amount);
        uint256 scalingFactor = _computeScalingFactor(IERC20(cashToken));
        amount = _upscale(amount, scalingFactor);
        collateralAmount[msg.sender][cashToken] = Math.add(int(amount), collateralAmount[msg.sender][cashToken]);
        if(traderIndex[msg.sender]==0){
            traders.push(msg.sender);
            traderIndex[msg.sender] = traders.length;
        }
    }

    function sendCollateral(address cashToken, uint256 amount, bytes32 poolId) override external onlyIssueManager(msg.sender, poolSecurity[poolId]){
        IERC20(cashToken).transfer(issuer[poolId], amount);
    }

    function onMatch(address party, address counterparty, bytes32 orderRef, address security, uint256 securityTraded, address currency, uint256 cashTraded) external 
                            override onlyIssueManager(msg.sender, security) returns(bool){
        IMarginOrder.order memory order = IMarginOrder(IMarginTradingPool(pools[security][currency]).getOrderbook()).getOrder(orderRef);
        uint256 marginCollateral = Math.add(FixedPoint.mulDown(cashTraded, IMarginTradingPool(pools[security][currency]).getMargin()),
                                            FixedPoint.mulDown(cashTraded, IMarginTradingPool(pools[security][currency]).getCollateral()));
        if(pretradeCheck(order, party, counterparty, security, currency, marginCollateral)){   
            emit subscribers(party, counterparty, security, securityTraded, currency, cashTraded);
            if(order.tokenIn==security && order.party==counterparty)
                collateralAmount[party][currency] = Math.sub(collateralAmount[party][currency], int(cashTraded));
            else
                collateralAmount[counterparty][currency] = Math.sub(collateralAmount[counterparty][currency], int(cashTraded));
            //record trade position
            IMarginManager.tradePosition memory position = IMarginManager.tradePosition({
                buyer : order.tokenIn==currency && order.party==counterparty ? counterparty : party,
                seller : order.tokenIn==security && order.party==counterparty ? counterparty : party,
                securityAmount: securityTraded,
                cashAmount : cashTraded,
                orderReference : orderRef,
                marginCollateral : marginCollateral,
                timestamp : 0
            });
            positions[security][currency].push(position);
            orders[orderRef] = positions[security][currency].length - 1;
            return true;
        }
        return false;
    }

    function pretradeCheck(IMarginOrder.order memory order, address party, address counterparty, address security, address currency, uint256 marginCollateral) private view returns(bool){
        //for security traded in by counterparty, party is trading in cash to buy security, so margin collateral needs to be checked
        if(order.tokenIn==security && order.party==counterparty){
            if(collateralAmount[party][currency] > int(marginCollateral))
                return true;
            else
                return false;
        }
        else{
            if(collateralAmount[counterparty][currency] > int(marginCollateral))
                return true;
            else
                return false;
        }
    }

    function onTrade(bytes32 ref, bytes32 cref, address security, uint256 securityTraded, address currency, uint256 currencyTraded, uint256 executionTime) external override onlyIssueManager(msg.sender, security) {
        if(securityTraded!=0 && currencyTraded!=0){ //if trade execution is successful, report trade and update timestamp
            uint256 pay = calculateFee(pools[security][currency], currency, currencyTraded);
            address orderbook = IMarginTradingPool(pools[security][currency]).getOrderbook();
            address party = IMarginOrder(orderbook).getOrder(cref).party;
            if(party==address(0x0))
                party = IMarginOrder(orderbook).getOrder(ref).party;
            collateralAmount[party][currency] = Math.sub(collateralAmount[party][currency], int(pay));
            IMarginOrder(orderbook).reportTrade(ref, cref, securityTraded, currencyTraded);
            uint256 pos = FixedPoint.divDown(currencyTraded, securityTraded);
            tradeReport(orderbook, ref, cref, securityTraded, party, pos, currencyTraded, pay);
            pos = orders[ref];
            IMarginManager.tradePosition memory position;
            if(pos!=0){
                position = positions[security][currency][pos];
                position.timestamp = executionTime;
            }
            pos = orders[cref];
            if(pos!=0){
                position = positions[security][currency][pos];
                position.timestamp = executionTime;
            }
        }
        else{ //if trade execution fails, add back cash 
            IMarginManager.tradePosition memory position;
            uint256 pos = orders[ref];
            if(pos!=0){
                position = positions[security][currency][pos];
                collateralAmount[position.buyer][currency] = Math.add(collateralAmount[position.buyer][currency], int(position.cashAmount));
                delete positions[security][currency][pos];
            }
            pos = orders[cref];
            if(pos!=0)
                position = positions[security][currency][pos];
                collateralAmount[position.buyer][currency] = Math.add(collateralAmount[position.buyer][currency], int(position.cashAmount));
                delete positions[security][currency][pos];
        }
    }

    function tradeReport(address orderbook, bytes32 ref, bytes32 cref, uint256 securityTraded,  
                            address buyer, uint256 price, uint256 currencyTraded, uint256 pay) private {
        address seller = IMarginOrder(orderbook).getOrder(ref).party;
        address security = IMarginOrder(orderbook).getSecurity();
        address currency = IMarginOrder(orderbook).getCurrency();
        bytes32 tradeRef = keccak256(abi.encodePacked(ref, cref));
        emit tradeSettled( 
            IMarginOrder(orderbook).getPoolId(),
            seller,
            buyer,
            securityTraded,
            security,
            price,
            currency,
            currencyTraded,
            tradeRef,
            pay
        );
    }    

    function calculateFee(address pool, address currency, uint256 currencyTraded) private returns(uint256){
        uint256 fee = IMarginTradingPool(pool).getFee();
        fee = FixedPoint.mulDown(currencyTraded, fee);
        uint256 pay = FixedPoint.divDown(fee, _computeScalingFactor(IERC20(currency)));
        fees[currency] = Math.add(fees[currency], pay);
        return pay;
    }

    function onSettle(address security, address currency, uint256 financingPerSec, bool charge, uint256 dividendPerSec, bool payout, uint256 settlementTime) override external onlyIssueManager(msg.sender, security){
        for(uint256 i=0; i<positions[security][currency].length; i++){
            IMarginManager.tradePosition memory position = positions[security][currency][i];
            if(collateralAmount[position.buyer][currency]!=0){
                if(charge)
                    collateralAmount[position.buyer][currency] = Math.sub(collateralAmount[position.buyer][currency], int(Math.mul(financingPerSec, Math.sub(settlementTime, position.timestamp))));
                else
                    collateralAmount[position.buyer][currency] = Math.add(collateralAmount[position.buyer][currency], int(Math.mul(financingPerSec, Math.sub(settlementTime, position.timestamp))));
                if(payout)
                    collateralAmount[position.buyer][currency] = Math.sub(collateralAmount[position.buyer][currency], int(Math.mul(dividendPerSec, Math.sub(settlementTime, position.timestamp))));
                else
                    collateralAmount[position.buyer][currency] = Math.add(collateralAmount[position.buyer][currency], int(Math.mul(dividendPerSec, Math.sub(settlementTime, position.timestamp))));
            }
            if(collateralAmount[position.seller][currency]!=0){
                if(payout)
                    collateralAmount[position.seller][currency] = Math.add(collateralAmount[position.seller][currency], int(Math.mul(dividendPerSec, Math.sub(settlementTime, position.timestamp))));
                else
                    collateralAmount[position.seller][currency] = Math.sub(collateralAmount[position.seller][currency], int(Math.mul(dividendPerSec, Math.sub(settlementTime, position.timestamp))));
            }
        }
    }    

    function withdraw(address security, address currency) override external onlyTrader(msg.sender){
        for(uint256 i=0; i<positions[security][currency].length; i++){
            IMarginManager.tradePosition memory position = positions[security][currency][i];
            if(position.buyer==msg.sender){
                IERC20(currency).transfer(position.buyer, uint(collateralAmount[position.buyer][currency]));
                delete positions[security][currency][orders[position.orderReference]];
            }
            else if(position.seller==msg.sender){
                 IERC20(currency).transfer(position.seller, uint(collateralAmount[position.seller][currency]));
                 delete positions[security][currency][orders[position.orderReference]];
            }
        }
    }

    function getTradingFeeCollected(address _token) override external returns(uint256){
        require(msg.sender==_distribution, "Unauthorized collection request");
        uint256 feeCollected = fees[_token];
        fees[_token] = Math.sub(fees[_token], feeCollected);
        IERC20(_token).transfer(_distribution, feeCollected);
        emit feecollection(address(this), feeCollected, block.timestamp);
        return feeCollected;
    }  

    /**
     * @dev This helper function is a fast and cheap way to convert between IERC20[] and IAsset[] types
     */
    function _convertERC20sToAssets(IERC20[] memory tokens) internal pure returns (IAsset[] memory assets) {
        // solhint-disable-next-line no-inline-assembly
        assembly {
            assets := tokens
        }
    }

    /**
     * @dev Returns a scaling factor that, when multiplied to a token amount for `token`, normalizes its balance as if
     * it had 18 decimals.
     */
    function _computeScalingFactor(IERC20 token) internal view returns (uint256) {
        // Tokens that don't implement the `decimals` method are not supported.
        uint256 tokenDecimals = ERC20(address(token)).decimals();

        // Tokens with more than 18 decimals are not supported.
        uint256 decimalsDifference = Math.sub(18, tokenDecimals);
        return FixedPoint.ONE * 10**decimalsDifference;
    }

    function _upscale(uint256 amount, uint256 scalingFactor) internal pure returns (uint256) {        
        return FixedPoint.mulDown(amount, scalingFactor);
    }

}
