// Settlement of trades
// (c) Kallol Borah, 2021 

pragma solidity 0.5.7;
pragma experimental ABIEncoderV2;

import "./interfaces/IPostTrade.sol";
import "./interfaces/IOrderPool.sol";
import "./interfaces/ITrade.sol";
import "./interfaces/ISecuritiesRegistry.sol";
import "./interfaces/VerifiedClient.sol";
import "./interfaces/VerifiedRates.sol";
import "./interfaces/ViaFactory.sol";
import "./interfaces/ViaCash.sol";
import "./registry/Security.sol";
import "abdk-libraries-solidity/ABDKMathQuad.sol";
import "@openzeppelin/contracts/ownership/Ownable.sol";
 
contract PostTrade is IPostTrade, Ownable {

    using ABDKMathQuad for uint256;
    using ABDKMathQuad for int256;
    using ABDKMathQuad for bytes16;

    mapping(bytes32 => IPostTrade.settlement) settlements;

    //mapping DPs to trades they are part of
    mapping(bytes32 => bytes32[]) private trades;

    bool success;
    address client;
    address trade;
    address securitiesregistry;
    ViaFactory private via;
    VerifiedRates private rates;

    event tradeSettled(address indexed transferor,
                        address indexed transferee,
                        uint256 unitsToTransfer,
                        address security,
                        uint256 price,
                        bytes32 currency,
                        bytes32 isin
                    );

    constructor() public Ownable() {
    }

    function initialize (address _client, address _trade, address _securitiesregistry, address _rates, address _viaFactory) public onlyOwner {
        client = _client;
        trade = _trade;
        securitiesregistry = _securitiesregistry;
        rates = VerifiedRates(_rates);
        via = ViaFactory(_viaFactory);
    }
    
    function postSettlement(IPostTrade.settlement calldata newTrade, bytes32 ref) external {
        require(trade==msg.sender);
        trades[newTrade.transferorDPID].push(ref);
        trades[newTrade.transfereeDPID].push(ref);
        settlements[ref].transferor = newTrade.transferor;
        settlements[ref].transferee = newTrade.transferee;
        settlements[ref].security = newTrade.security;
        settlements[ref].securityName = newTrade.securityName;
        settlements[ref].isin = newTrade.isin;
        settlements[ref].company = newTrade.company;
        settlements[ref].consideration = newTrade.consideration;
        settlements[ref].price = newTrade.price;
        settlements[ref].currency = newTrade.currency;
        settlements[ref].unitsToTransfer = newTrade.unitsToTransfer;
        settlements[ref].executionDate = newTrade.executionDate;
        settlements[ref].transferorDPID = newTrade.transferorDPID;
        settlements[ref].transfereeDPID = newTrade.transfereeDPID;
        settlements[ref].status = newTrade.status;
        settlements[ref].orderPool = newTrade.orderPool;
        settlements[ref].partyRef = newTrade.partyRef;
        settlements[ref].counterpartyRef = newTrade.counterpartyRef;
    }

    function getSettlementRequests(bytes32 dpid) external view returns(bytes32[] memory){
        (bytes32 role, ) = VerifiedClient(client).getRole(msg.sender);
        if(role=="DP"){
            return trades[dpid];
        } 
    }

    function getSettlementRequest(bytes32 ref) external view returns(settlement memory){
        require(ref!=""); 
        IPostTrade.settlement memory response = IPostTrade.settlement({
            transferor : settlements[ref].transferor,
            transferee : settlements[ref].transferee,
            security : settlements[ref].security,
            securityName : settlements[ref].securityName,
            status : settlements[ref].status,
            transferorDPID : settlements[ref].transferorDPID,
            transfereeDPID : settlements[ref].transfereeDPID,
            isin : settlements[ref].isin,
            company : settlements[ref].company,
            currency : settlements[ref].currency,
            price : settlements[ref].price,
            consideration : settlements[ref].consideration,
            unitsToTransfer : settlements[ref].unitsToTransfer,
            executionDate : settlements[ref].executionDate,
            orderPool : settlements[ref].orderPool,
            partyRef : settlements[ref].partyRef,
            counterpartyRef : settlements[ref].counterpartyRef
        });
        return response;                                                              
    }
    
    function setSettlementStatus(bytes32 ref, bytes32 status) external{
        require(status=="Confirm" || status=="Reject");
        (bytes32 role, bytes32 id) = VerifiedClient(client).getRole(msg.sender);
        if(role=="DP"){
            settlements[ref].status = status;
            if(status=="Confirm"){
                address cashToken = via.getTokenByNameType(settlements[ref].currency, "ViaCash");
                uint256 fee = SafeMath.mul(SafeMath.mul(settlements[ref].price, settlements[ref].unitsToTransfer), rates.getFee("trading"));
                uint256 pay = SafeMath.sub(SafeMath.mul(settlements[ref].price, settlements[ref].unitsToTransfer), fee);
                if(ViaCash(cashToken).transferFrom(settlements[ref].transferee, settlements[ref].transferor, pay)){                
                    success = Security(settlements[ref].security).transferFrom(settlements[ref].transferor, 
                                                                    settlements[ref].transferee, 
                                                                    settlements[ref].unitsToTransfer);
                    if(rates.getFeeToSetter()!=address(0x0) && success && fee!=0){
                        success = ViaCash(cashToken).transferFrom(settlements[ref].transferee, rates.getFeeToSetter(), fee);
                    }
                    if(success){
                        ISecuritiesRegistry(securitiesregistry).registerPrice(settlements[ref].isin, 
                                                                            ABDKMathQuad.fromUInt(settlements[ref].price),
                                                                            settlements[ref].transferor, 
                                                                            settlements[ref].transferee);
                        IOrderPool(settlements[ref].orderPool).orderFilled(settlements[ref].partyRef, settlements[ref].counterpartyRef);
                        ITrade(trade).tradeSettled(ref, settlements[ref].partyRef, settlements[ref].counterpartyRef);
                        emit tradeSettled(settlements[ref].transferor, 
                                            settlements[ref].transferee, 
                                            settlements[ref].unitsToTransfer,
                                            settlements[ref].security,
                                            settlements[ref].price,
                                            settlements[ref].currency,
                                            settlements[ref].isin
                                        );
                        for(uint i=0; i<trades[id].length; i++){
                            if(trades[id][i]==ref){
                                if(i==trades[id].length-1)
                                    delete trades[id][i];
                                else
                                    trades[id][i] = trades[id][i+1];
                            }
                        }
                    }
                    else{
                        IOrderPool(settlements[ref].orderPool).revertTrade(settlements[ref].partyRef, settlements[ref].unitsToTransfer, "Sell");
                        IOrderPool(settlements[ref].orderPool).revertTrade(settlements[ref].counterpartyRef, settlements[ref].unitsToTransfer, "Buy");
                        ViaCash(cashToken).transferFrom(settlements[ref].transferor, settlements[ref].transferee, pay);
                    }
                }
            }
            else if(status=="Reject"){
                IOrderPool(settlements[ref].orderPool).revertTrade(settlements[ref].partyRef, settlements[ref].unitsToTransfer, "Sell");
                IOrderPool(settlements[ref].orderPool).revertTrade(settlements[ref].counterpartyRef, settlements[ref].unitsToTransfer, "Buy");
            }            
        }
    }

}
