pragma solidity ^0.7.1;
pragma experimental ABIEncoderV2;

import "@balancer-labs/v2-solidity-utils/contracts/openzeppelin/IERC20.sol";
import "@balancer-labs/v2-solidity-utils/contracts/openzeppelin/ERC20.sol";
import "@balancer-labs/v2-solidity-utils/contracts/openzeppelin/Ownable.sol";
import "@balancer-labs/v2-solidity-utils/contracts/math/Math.sol";
import "@balancer-labs/v2-solidity-utils/contracts/math/FixedPoint.sol";

import "@balancer-labs/v2-vault/contracts/interfaces/IVault.sol";
import "@balancer-labs/v2-vault/contracts/interfaces/IAsset.sol";

import "./interfaces/IPrimaryIssuePoolFactory.sol";
import "./interfaces/IPrimaryIssuePool.sol";

import "./interfaces/VerifiedClient.sol";
import "./interfaces/IMarketMaker.sol";
import "./interfaces/ILiquidity.sol";
import "./interfaces/IFactory.sol";
import "./interfaces/ISecurity.sol";
import "./interfaces/IDistribution.sol";

contract PrimaryIssueManager is IMarketMaker, Ownable {

    using Math for uint256;
    using FixedPoint for uint256;

    // balancer variables
    address private factory;
    address private vault;
    uint256 private swapFeePercentage=0;

    uint256 private constant _INITIAL_BPT_SUPPLY = 2**(112) - 1;
    uint256 private constant _DEFAULT_MINIMUM_BPT = 1e6;

    // reference to the Verified factory contract that this asset manager checks security token issues with
    IFactory private products;

    // reference to the Verified Client contract
    VerifiedClient private client;

    // reference to the Verified Liquidity contract that provides liquidity (ie, VITTA) to the asset manager to underwrite investments in tokenized securities
    address private LiquidityContract;

    //distribution contract 
    address private distribution;

    //mutex
    bool private lock;

    // modifiers
    modifier onlyPool(bytes32 poolId, address caller) {
        require(caller == pools[poolId], "Invalid pool");
        _;
    }

    modifier onlyLP(address sender) {
        require(sender==LiquidityContract, "Only Liquidity contract");
        _;
    }

    modifier onlyAssetManager(address sender){
        (bytes32 role, , ) = client.getRole(sender);
        require(role=="AM" || role=="DP", "Only Asset manager");
        _;
    }   

    modifier onlyDP(address sender){
        (bytes32 role, , ) = client.getRole(sender);
        require(role=="DP", "Only Depositary participant");
        _;
    } 

    modifier onlyIssueManager(address sender, address security){
        address DP = products.getDP(security);
        require(DP==sender, "Only Issue manager");
        _;
    }

    // list of offered tokens
    address[] internal offeredTokens;

    // mapping token offered to its position in the list of offered tokens
    mapping(address => uint256) private offeredTokenIndex;

    // list of tokens to match
    mapping(address => token[]) internal toMatchTokens;

    // mapping offered token (eg, security token) to a token to match (eg, settlement token)
    mapping(address => mapping(address => token[])) internal mmtokens;

    // mapping offered token (ie security token) to its paired token 
    mapping(address => address[]) internal pairedTokens;

    // mapping liqudity token to its position in the list of liquidity tokens
    mapping(address => mapping(address => uint256)) private pairedTokenIndex;    

    // mapping qualified liquidity token to its price and amount offered by market makers (LPs)
    mapping(address => mapping(address => liquidity)) internal qlTokens;

    // mapping security to liquidity providers
    mapping(address => lp[]) private liquidityProviders;

    // mapping securities issued in the primary issue pool to total liquidity offered
    mapping(address => uint256) private totalUnderwritten;

    // mapping pool ids to LPs to the amounts of liquidity they offer
    mapping(bytes32 => mapping(address => uint256)) private underwriters;    

    // mapping security to new issues in the primary issue pool
    mapping(address => primary) internal issues;

    // mapping primary issue pool id to subscribers that swap in assets into pool
    mapping(bytes32 => subscriptions[]) internal investors; 

    // mapping primary issue pool id to subscriber and its position in the list of subscribers to the pool
    mapping(bytes32 => mapping(address => uint256)) private subscriberIndex;

    // mapping pool id to asset to subscription amounts
    mapping(bytes32 => mapping(address => uint256)) private subscribed;

    // mapping pool id to pool address
    mapping(bytes32 => address) private pools;

    // mapping pool id to security token offered
    mapping(bytes32 => address) private poolSecurity;   
    
    // LP token allotted
    mapping(address => mapping (address => uint256)) private LPTokenAllotted;

    // mapping token address to issuing fee collected
    mapping(address => uint256) private fees;
    
    // mapping security to currency to fee set for it by DP
    mapping(address => mapping(address => uint256)) private customFee;
    
    // revenue share for asset managers
    uint256 private revshare;

    event marketmakers(address indexed security, address platform, address provider, address currency, uint256 tokenOffered, uint256 underwritten, uint256 earnings);
    event subscribers(address indexed security, bytes32 poolId, address investor, address currency, uint256 cashSwapped, uint256 securitySwapped, uint256 timestamp, bool subscription);
    event offers(address party, address indexed offered, uint256 amountOffered, address tomatch, uint256 amountDesired, uint256 minAmount, bytes32 isin, uint256 minimumOrderSize, string offeringDocs);
    event closures(bytes32 indexed poolId, address security, uint256 timestamp);
    event allotments(bytes32 indexed poolId, address investor, address security, uint256 securitySubscription, address currency, uint256 allotedAmount, uint256 timestamp);
    event refunds(bytes32 indexed poolId, address investor, address security, uint256 securitySubscription, address currency, uint256 refundAmount, uint256 timestamp);
    event settlements(bytes32 indexed poolId, address security, address currency, address liquidityProvider, uint256 underwritingFee, address issuer, uint256 subscription);
    event feecollection(address indexed platform, uint256 collection, uint256 timestamp);

    /**
        Initializes this asset management contract
        @param  _factory            reference to the primary issue pool factory contract
        @param  _swapFeePercentage  percentage of trading fee to be charged by the asset manager
        @param  _vault              reference to the Balancer vault
        @param  _products           reference to the Verified Products contract that this contract reports created primary issue pools to
     */
    function initialize(address _factory, uint256 _swapFeePercentage, address _vault, address _products, address _liquidity, address _client, address _distribution) public onlyOwner {
        factory = _factory;
        vault = _vault;        
        products = IFactory(_products);        
        client = VerifiedClient(_client);
        swapFeePercentage = _swapFeePercentage;
        LiquidityContract = _liquidity;
        distribution = _distribution;
    }

    /**
        Called by market maker and issuer for adding liquidity
        @param  owned       is the security or settlement token on offer 
        @param  offered     is the amount offered
        @param  tomatch     is the liquidity token that is required to be paired
        @param  desired     is the amount required of the liquidity token to be paired
        @param  min         is the minimum amount to be swapped of the 'offered' token 
        @param  isin        is the identifier for the security token offered
        @param  owner       is the current holder of the owned token that is offered
        @param  docs        is the ipfs address of the offering docs
     */ 
    function offer(address owned, bytes32 isin, uint offered, address tomatch, uint desired, uint min, address owner, string memory docs) override external {
        require(!lock, "Access locked");
        lock = true;
        (bytes32 role, , ) = client.getRole(msg.sender);
        require(products.checkProduct(owned)==true || role=="AM", "Unauthorized access");
        if(role=="AM"){
            if(ERC20(owned).balanceOf(address(this))>=LPTokenAllotted[msg.sender][owned] && LPTokenAllotted[msg.sender][owned]>=offered){                 
                LPTokenAllotted[msg.sender][owned] = SafeMath.sub(LPTokenAllotted[msg.sender][owned], offered);
                make(msg.sender, owned, isin, offered, tomatch, desired, min, docs); 
            }
            else if(ERC20(owned).balanceOf(msg.sender)>=offered){
                ERC20(owned).transferFrom(msg.sender, address(this), offered);
                make(msg.sender, owned, isin, offered, tomatch, desired, min, docs);
            }
            emit offers(msg.sender, owned, offered, tomatch, desired, min, isin, 1e18, docs);
        }
        else if(ERC20(owned).balanceOf(owner)>=offered){             
            ERC20(owned).transferFrom(owner, address(this), offered);
            make(owner, owned, isin, offered, tomatch, desired, min, docs); 
        }
        lock = false;
    }

    function make(address _owner, address owned, bytes32 _isin, uint _offered, address tomatch, uint _desired, uint _min, string memory _docs) private {
        IMarketMaker.token memory t = IMarketMaker.token({
            owner: _owner,
            offered: owned,
            amountDesired: _desired,
            amountOffered: _offered,
            min: _min,
            isin: _isin,
            minimumOrderSize: 1e18,
            offeringdocs: _docs
        });
        uint256 index = mmtokens[owned][tomatch].length;
        mmtokens[owned][tomatch].push(t);        
        if(offeredTokenIndex[owned]==0){            
            offeredTokens.push(owned);
            offeredTokenIndex[owned] = offeredTokens.length;
        }
        toMatchTokens[tomatch].push(mmtokens[owned][tomatch][index]);                      
    }

    function setOfferTerms(address owner, address owned, address tomatch, uint256 minOrderSize) override external onlyIssueManager(msg.sender, owned) {
        IMarketMaker.token storage t = mmtokens[owned][tomatch][0];
        if(t.owner==owner){
            t.minimumOrderSize = _upscale(minOrderSize, _computeScalingFactor(IERC20(owned)));
        }
    }

    function setIssuingFee(address owner, address owned, address tomatch, uint256 swapfee) override external onlyIssueManager(msg.sender, owned) {
        IMarketMaker.token storage t = mmtokens[owned][tomatch][0];
        if(t.owner==owner){
            customFee[owned][tomatch]=swapfee;
        }
    }

    /**
        Gets tokens to match for offered token
        @param  offered address of offered token
     */
    function getOffered(address offered) override external view onlyAssetManager(msg.sender) returns(IMarketMaker.token[] memory){
        return toMatchTokens[offered];
    }

    /**
        Gets offer made previously
        @param  _owned      address of token offered
        @param  _tomatch    address of token to match
     */
    function getOfferMade(address _owned, address _tomatch) override external view onlyAssetManager(msg.sender) returns(IMarketMaker.token[] memory){
        return mmtokens[_owned][_tomatch];
    }

    /**
        Fetches liquidity providers for a security token
        @param  _security   identifier for the security token offered
     */
    function getLiquidityProviders(address _security) override external view returns(lp[] memory){        
        return liquidityProviders[_security];
    }

    /**
        Called by Liquidity token contract to stake tokens by investors
        @param  _amount     amount of tokens staked to provide liquidity for underwriting investments in tokenized securities
        @param  _token      address of token staked
        @param  _manager    address of asset manager
     */
    function stake(uint256 _amount, address _token, address _manager) override external onlyLP(msg.sender) {
        LPTokenAllotted[_manager][_token] = SafeMath.add(LPTokenAllotted[_manager][_token], _amount);
    }

    /**
        Gets liquidity allotted for asset manager (message sender)
        @param  _token  address of token allocated earlier to asset manager (message sender)
     */
    function getAllotedStake(address _token) override external view returns(uint256){
        return LPTokenAllotted[msg.sender][_token];
    }    
    
    /**
        Called by issuer of 'security' token to open an issue which will last till 'cutoffTime'
        @param  security    security offered to the primary issue pool
        @param  cutoffTime  time in milliseconds by when offer closes
     */
    function issue(address security, uint256 cutoffTime, address issuer) override external onlyDP(msg.sender) {
        onIssue(security, cutoffTime, issuer);
    }
    
    function onIssue(address security, uint256 cutoffTime, address issuer) private {
        // check if security to be issued has been offered by the issuer, and if yes, initialize the issuance
        
        address marketmaker;
        uint256 matchedTokens;
        address cash;
        uint256 securityOffered;
        uint256 minCashDesired;
        uint256 cashOffered;

        if(offeredTokens[offeredTokenIndex[security]-1]==security){            
            // check all offered securities that is not the security to be issued
            for(uint j=0; j<offeredTokens.length; j++){
                
                if(offeredTokens[j]!=security){                    
                    cash = offeredTokens[j];                     
                    
                    // check if the request to issue has come from the issuer
                    //if(mmtokens[security][cash][0].owner==issuer && mmtokens[security][cash][0].amountOffered>0){
                        
                        matchedTokens = mmtokens[cash][security].length;
                        for(uint k=0; k<matchedTokens; k++){

                            securityOffered = mmtokens[security][cash][0].amountOffered;
                            minCashDesired = mmtokens[security][cash][0].min;
                            cashOffered = mmtokens[cash][security][k].amountOffered;
                            marketmaker = mmtokens[cash][security][k].owner;
                            
                            // find tokens offered against security to be issued
                            if(cashOffered!=0){
                                
                                // if min offer price for security issued is less than price desired by market maker (LP)
                                if( Math.div(minCashDesired,
                                    securityOffered, true) <=
                                    Math.div(cashOffered,
                                    mmtokens[cash][security][k].amountDesired, true))
                                {   
                                    // store liquidity tokens    
                                    if(pairedTokenIndex[security][cash]==0){
                                        pairedTokens[security].push(cash);
                                        pairedTokenIndex[security][cash] = pairedTokens[security].length;                                            
                                    }
                                    
                                    // find and store min price for token offered
                                    if(Math.div(minCashDesired,
                                        securityOffered, true) < 
                                        qlTokens[security][cash].minPrice  || 
                                        qlTokens[security][cash].minPrice==0)
                                    {   
                                        qlTokens[security][cash].minPrice =
                                            FixedPoint.divDown(_upscale(minCashDesired, _computeScalingFactor(IERC20(cash))),
                                                    _upscale(securityOffered, _computeScalingFactor(IERC20(security))));
                                    }                                    
                                     
                                    qlTokens[security][cash].amountIssued = 
                                                    //Math.add(qlTokens[security][cash].amountIssued,
                                                    securityOffered;
                                                    //);
                                    qlTokens[security][cash].amountOffered = 
                                                    Math.add(qlTokens[security][cash].amountOffered,
                                                    cashOffered); 
                                    
                                    // store qualified liquidity provider info
                                    lp memory provider = lp({
                                        owner : marketmaker,
                                        tokenOffered : cash,
                                        underwritten : cashOffered,
                                        subscribed : 0,
                                        earned : 0
                                    });
                                    liquidityProviders[security].push(provider);  
                                    emit marketmakers(security, address(this), marketmaker, cash, cashOffered, provider.subscribed, provider.earned);

                                    totalUnderwritten[security] = 
                                        SafeMath.add(totalUnderwritten[security], cashOffered);
                                    cashOffered = 0;
                                }
                                else{
                                    //adjust amount underwritten from asset manager's allocated stake                                            
                                    LPTokenAllotted[marketmaker][cash] = 
                                        SafeMath.add(LPTokenAllotted[marketmaker][cash], cashOffered);
                                }
                            }
                        }
                    //}
                }
            }
            if(pairedTokens[security].length >0){
                createPool(security, cutoffTime);
                delete pairedTokens[security];
            }
        }
    }
    
    function createPool(address security, uint256 cutoffTime) internal {
        uint256 matchedTokens = pairedTokens[security].length;
        //create primary issue pool if it does not exist already
        for(uint x=0; x<matchedTokens; x++){
            address lptoken = pairedTokens[security][x];
            uint256 securityAmount = qlTokens[security][lptoken].amountIssued;
            uint256 cashAmount = qlTokens[security][lptoken].amountOffered;            
            IPrimaryIssuePoolFactory.FactoryPoolParams memory poolparams = IPrimaryIssuePoolFactory.FactoryPoolParams({
                name : "Verified Pool Token",
                symbol : "VPT",
                security : security,
                currency : lptoken,
                minimumPrice: qlTokens[security][lptoken].minPrice,
                minimumOrderSize : mmtokens[security][lptoken][0].minimumOrderSize,
                maxAmountsIn : securityAmount,
                swapFeePercentage : swapFeePercentage,
                cutOffTime : cutoffTime,
                offeringDocs: mmtokens[security][lptoken][0].offeringdocs
            });                
            ISecurity(security).whiteList(vault, securityAmount); 
            address newIssue = IPrimaryIssuePoolFactory(factory).create(poolparams);  
            // store details of new pool created
            issues[security].issuer = mmtokens[security][lptoken][0].owner;
            issues[security].deadline = cutoffTime;
            bytes32 pool = IPrimaryIssuePool(newIssue).getPoolId();                
            issues[security].pools.push(pool);
            pools[pool] = newIssue;
            poolSecurity[pool] = security;
            // join the pool here   
            joinPool(pool, security, lptoken, securityAmount, cashAmount);
            delete qlTokens[security][lptoken];
            pairedTokenIndex[security][lptoken]=0;
        }
    }
    
    function joinPool(bytes32 pool, address security, address lptoken, uint256 securityAmount, uint256 cashAmount) private {
        (IERC20[] memory tokens, , ) = IVault(vault).getPoolTokens(pool);
        IAsset[] memory assets = _convertERC20sToAssets(tokens);
        uint256[] memory maxAmountsIn = new uint256[](tokens.length);                
        ERC20(lptoken).approve(vault, cashAmount);
        ERC20(security).approve(vault, securityAmount);
        for(uint256 t; t < tokens.length; t++) {
            if(tokens[t]==IERC20(security))
                maxAmountsIn[t] = securityAmount;
            else if(tokens[t]==IERC20(lptoken))
                maxAmountsIn[t] = cashAmount;
            else 
                maxAmountsIn[t] = _INITIAL_BPT_SUPPLY - _DEFAULT_MINIMUM_BPT;
        }
        IVault.JoinPoolRequest memory request = IVault.JoinPoolRequest({
            assets: assets,
            maxAmountsIn: maxAmountsIn,
            userData: abi.encode(JoinKind.INIT, maxAmountsIn),
            fromInternalBalance: false
        });         
        IVault(vault).joinPool(pool, address(this), address(this), request);
    }

    // called by pool when assets are swapped in by investors against security tokens issued
    function subscribe(bytes32 poolId, address security, address asset, uint256 cAmount, address investor, uint256 sAmount, bool paidIn) override external onlyPool(poolId, msg.sender) {
        uint256 index = subscriberIndex[poolId][investor];
        uint256 amount = _downscaleDown(cAmount, _computeScalingFactor(IERC20(asset)));
        emit subscribers(poolSecurity[poolId], poolId, investor, asset, amount, sAmount, block.timestamp, paidIn);
        if(paidIn){
            subscribed[poolId][asset] = Math.add(subscribed[poolId][asset], amount);
            if(index==0){
                IMarketMaker.subscriptions memory subscription = IMarketMaker.subscriptions({
                    investor: investor,
                    asset: asset,
                    name: ERC20(asset).name(),
                    amount: amount,
                    traded: _downscaleDown(sAmount, _computeScalingFactor(IERC20(security)))
                });
                investors[poolId].push(subscription);   
                subscriberIndex[poolId][investor] = investors[poolId].length; 
            }
            else{
                IMarketMaker.subscriptions storage subscription = investors[poolId][index-1];
                if(subscription.investor==investor){
                    subscription.amount = Math.add(subscription.amount, amount);
                    subscription.traded = Math.add(subscription.traded, _downscaleDown(sAmount, _computeScalingFactor(IERC20(security))));
                }
            }        
        }
        else{
            if(index > 0){
                IMarketMaker.subscriptions storage subscription = investors[poolId][index-1];
                if(subscription.investor==investor){
                    subscription.amount = Math.sub(subscription.amount, amount);
                    subscription.traded = Math.sub(subscription.traded, _downscaleDown(sAmount, _computeScalingFactor(IERC20(security))));
                }
                if(subscribed[poolId][asset] >= amount)
                    subscribed[poolId][asset] = Math.sub(subscribed[poolId][asset], amount);
            }
        }
    }

    /**
        Fetches investors (subscribers) to a primary issue pool
        @param   poolId identifier for a primary issue pool for which subscribers are to be returned
     */
    function getSubscribers(bytes32 poolId) override external view onlyIssueManager(msg.sender, poolSecurity[poolId]) returns(IMarketMaker.subscriptions[] memory){
        return investors[poolId];
    }

    /**
        Called by issuer to close subscription of 'security' issued by it
        @param  security    address of security token
     */  
    function close(address security) override external onlyIssueManager(msg.sender, security) {
        address currency;
        bytes32 poolId;
        uint256 length = issues[security].pools.length;
        for(uint256 i=0; i<length; i++){  
            poolId = issues[security].pools[i];
            currency = IPrimaryIssuePool(pools[poolId]).getCurrency(); 
            // exit the pool here   
            (IERC20[] memory tokens, , ) = IVault(vault).getPoolTokens(poolId);
            IAsset[] memory assets = _convertERC20sToAssets(tokens);
            uint256[] memory amountsOut = new uint256[](tokens.length);
            IVault.ExitPoolRequest memory request = IVault.ExitPoolRequest({
                assets: assets,
                minAmountsOut: amountsOut,
                userData: abi.encode(ExitKind.EXACT_BPT_IN_FOR_TOKENS_OUT, _INITIAL_BPT_SUPPLY - _DEFAULT_MINIMUM_BPT),
                toInternalBalance: false
            });         
            IVault(vault).exitPool(poolId, address(this), payable(address(this)), request);
            emit closures(poolId, security, block.timestamp);
            IERC20(security).transfer(issues[security].issuer, IERC20(security).balanceOf(address(this)));
        }
    }    

    /**
        Called by issuer to accept subscription to issue by investor
        @param  poolid      identifier of primary issue pool in which subscription is made by investor
        @param  investor    address of investor (subscriber)
        @param  amnt        amount of investment (subscription capital) accepted by issuer for which allotment of security tokens are made to investor
        @param  asset       address of asset which is used by investor to subscribe to the issue
     */ 
    function accept(bytes32 poolid, address investor, uint256 amnt, address asset) override external onlyIssueManager(msg.sender, poolSecurity[poolid]) {
        onAccept(poolid, investor, amnt, asset);
    }
        
    function onAccept(bytes32 poolid, address investor, uint256 amnt, address asset) private {
        uint256 i = subscriberIndex[poolid][investor];
        require(i>0, "No subscriber found");
        i = i-1;
        require(investors[poolid][i].investor==investor, "Invalid allotment subscriber");
        require(investors[poolid][i].amount >= amnt, "Incorrect allotment amount");
        
        uint256 prorataAllotment;
        uint256 securitiesAlloted;
        uint256 securityReturned;
        uint256 cashRefund;
        // transfer subscriptions for allotments to asset manager for asset subscribed with
        address issued = IPrimaryIssuePool(pools[poolid]).getSecurity();
        // refund balance to investors
        prorataAllotment = FixedPoint.divDown(amnt, investors[poolid][i].amount);
        investors[poolid][i].amount = Math.sub(investors[poolid][i].amount, amnt);
        cashRefund = investors[poolid][i].amount;
        securityReturned = Math.sub(investors[poolid][i].traded, FixedPoint.mulDown(investors[poolid][i].traded, prorataAllotment));
        securitiesAlloted = Math.add(securitiesAlloted, FixedPoint.mulDown(investors[poolid][i].traded, prorataAllotment));          
        subscribed[poolid][asset] = Math.sub(subscribed[poolid][asset], cashRefund);
        emit refunds(poolid, investor, issued, securityReturned, asset, cashRefund, block.timestamp);                                
        subscriberIndex[poolid][investor] = 0;
        emit allotments(poolid, investor, issued, securitiesAlloted, asset, amnt, block.timestamp);

        IERC20(issued).transferFrom(investor, mmtokens[issued][asset][0].owner, securityReturned);
        IERC20(asset).transfer(investor, cashRefund);
    }
    
    /**
        Called by issuer to reject subscription to issue by investor
        @param  poolid      identifier for primary issue pool to which investor has subscribed
        @param  investor    address of investor (subscriber)
     */ 
    function reject(bytes32 poolid, address investor) override external onlyIssueManager(msg.sender, poolSecurity[poolid]) {
        uint256 i = subscriberIndex[poolid][investor];
        require(i>0, "No subscriber found");
        i = i-1;
        require(investors[poolid][i].investor==investor, "Incorrect subscriber for refund");
        
        address issuedSecurity = poolSecurity[poolid];
        address currencySubscribed = investors[poolid][i].asset;         
        subscribed[poolid][currencySubscribed] = Math.sub(subscribed[poolid][currencySubscribed], investors[poolid][i].amount);
        emit refunds(poolid, investor, issuedSecurity, investors[poolid][i].traded, currencySubscribed, investors[poolid][i].amount, block.timestamp);              
        subscriberIndex[poolid][investor] = 0;    
        IERC20(issuedSecurity).transferFrom(investor, mmtokens[issuedSecurity][currencySubscribed][0].owner, investors[poolid][i].traded);
        IERC20(currencySubscribed).transfer(investor, investors[poolid][i].amount);                 
    }    
    
    /**
        Called by product issuer to settle distribution of fee income arising from investment underwritten in the primary issue pool
        @param  poolId  identifier for primary issue pool
     */  
    function settle(bytes32 poolId) override external onlyIssueManager(msg.sender, poolSecurity[poolId]) {
        (, , revshare,) = IDistribution(distribution).getRevenueShares();
        onSettle(poolId);
    }
    
    function onSettle(bytes32 poolId) private {        
        address currency = IPrimaryIssuePool(pools[poolId]).getCurrency(); 
        address issuer = mmtokens[poolSecurity[poolId]][currency][0].owner;
        uint256 subscription = _upscale(subscribed[poolId][currency], _computeScalingFactor(IERC20(currency)));
        uint256 issuingFee = customFee[poolSecurity[poolId]][currency]==0 ? FixedPoint.mulDown(subscription, swapFeePercentage) 
                                                : FixedPoint.mulDown(subscription, customFee[poolSecurity[poolId]][currency]);
        uint256 prorataLiquidityProvided =0;
        uint256 underwritingFee = 0;        
        for(uint i=0; i<liquidityProviders[poolSecurity[poolId]].length; i++){
            prorataLiquidityProvided = FixedPoint.divDown(liquidityProviders[poolSecurity[poolId]][i].underwritten, totalUnderwritten[poolSecurity[poolId]]);
            underwritingFee = FixedPoint.divDown(FixedPoint.mulDown(issuingFee, prorataLiquidityProvided), _computeScalingFactor(IERC20(currency)));
            ILiquidity(LiquidityContract).distribute(underwritingFee, 
                                                    liquidityProviders[poolSecurity[poolId]][i].tokenOffered,
                                                    liquidityProviders[poolSecurity[poolId]][i].owner);
            IERC20(currency).transfer(liquidityProviders[poolSecurity[poolId]][i].owner, FixedPoint.mulDown(underwritingFee, revshare));
            liquidityProviders[poolSecurity[poolId]][i].earned = Math.add(liquidityProviders[poolSecurity[poolId]][i].earned, underwritingFee);
            prorataLiquidityProvided = FixedPoint.divDown(FixedPoint.mulDown(prorataLiquidityProvided, Math.sub(subscription, issuingFee)), _computeScalingFactor(IERC20(currency)));
            SafeMath.add(liquidityProviders[poolSecurity[poolId]][i].subscribed, prorataLiquidityProvided);
            emit settlements(poolId, poolSecurity[poolId], currency, liquidityProviders[poolSecurity[poolId]][i].owner, underwritingFee, issuer, prorataLiquidityProvided);
        }
        issuingFee = FixedPoint.divDown(issuingFee, _computeScalingFactor(IERC20(currency)));
        fees[currency] = Math.add(fees[currency], FixedPoint.sub(issuingFee, FixedPoint.mulDown(issuingFee, revshare)));
        IERC20(currency).transfer(issuer, Math.sub(subscribed[poolId][currency], issuingFee));
        delete investors[poolId];                 
    }   

    function getIssuingFeeCollected(address _token) override external returns(uint256){
        require(msg.sender==distribution, "Unauthorized fee collection");
        uint256 feeCollected = fees[_token];        
        fees[_token] = Math.sub(fees[_token], feeCollected);
        IERC20(_token).transfer(distribution, feeCollected);
        emit feecollection(address(this), feeCollected, block.timestamp);
        return feeCollected;
    }

    function getOwner() override external view returns(address){
        return owner();
    }

    /**
     * @dev This helper function is a fast and cheap way to convert between IERC20[] and IAsset[] types
     */
    function _convertERC20sToAssets(IERC20[] memory tokens) internal pure returns (IAsset[] memory assets) {
        // solhint-disable-next-line no-inline-assembly
        assembly {
            assets := tokens
        }
    }

    /**
     * @dev Returns a scaling factor that, when multiplied to a token amount for `token`, normalizes its balance as if
     * it had 18 decimals.
     */
    function _computeScalingFactor(IERC20 token) internal view returns (uint256) {
        // Tokens that don't implement the `decimals` method are not supported.
        uint256 tokenDecimals = ERC20(address(token)).decimals();

        // Tokens with more than 18 decimals are not supported.
        uint256 decimalsDifference = Math.sub(18, tokenDecimals);
        return FixedPoint.ONE * 10**decimalsDifference;
    }

    /**
     * @dev Reverses the `scalingFactor` applied to `amount`, resulting in a smaller or equal value depending on
     * whether it needed scaling or not. The result is rounded down.
     */
    function _downscaleDown(uint256 amount, uint256 scalingFactor) internal pure returns (uint256) {
        return FixedPoint.divDown(amount, scalingFactor);
    }

    /**
     * @dev Applies `scalingFactor` to `amount`, resulting in a larger or equal value depending on whether it needed
     * scaling or not.
     */
    function _upscale(uint256 amount, uint256 scalingFactor) internal pure returns (uint256) {
        // Upscale rounding wouldn't necessarily always go in the same direction: in a swap for example the balance of
        // token in should be rounded up, and that of token out rounded down. This is the only place where we round in
        // the same direction for all amounts, as the impact of this rounding is expected to be minimal (and there's no
        // rounding error unless `_scalingFactor()` is overriden).
        return FixedPoint.mulDown(amount, scalingFactor);
    }

}

