pragma solidity 0.7.1;
pragma experimental ABIEncoderV2;

import "./interfaces/ISettlor.sol";
import "./interfaces/IOrder.sol";
import "./interfaces/ITrade.sol";
import "./interfaces/IFactory.sol";
import "./interfaces/ISecurity.sol";
import "./interfaces/VerifiedClient.sol";
import "./interfaces/ISecondaryIssuePool.sol";
import "./interfaces/ISecondaryIssuePoolFactory.sol";

import "@balancer-labs/v2-solidity-utils/contracts/math/Math.sol";
import "@balancer-labs/v2-solidity-utils/contracts/math/FixedPoint.sol";
import "@balancer-labs/v2-solidity-utils/contracts/openzeppelin/ERC20.sol";
import "@balancer-labs/v2-solidity-utils/contracts/openzeppelin/IERC20.sol";
import "@balancer-labs/v2-solidity-utils/contracts/openzeppelin/Ownable.sol";

contract SecondaryIssueManager is ISettlor, Ownable {

    using Math for uint256;
    using FixedPoint for uint256;

    // balancer variables
    address private factory;    

    address private vault;

    //fee
    uint256 private fee;

    // mapping settlement ref to trade execution data
    mapping(bytes32 => ISettlor.settlement) settlements;

    //mapping depository participants to trades they are part of
    mapping(bytes32 => bytes32[]) private trades;

    //mapping pool address to pool id
    mapping(address => bytes32) private pools;

    //mapping pool id to security token traded
    mapping(bytes32 => address) private poolSecurity;

    //mapping pool id to investors
    mapping(bytes32 => subscriptions[]) internal investors; 

    // mapping token address to issuing fee collected
    mapping(address => uint256) private fees;

    // reference to the Verified Client contract
    VerifiedClient private client;

    // reference to the Verified factory contract that this asset manager checks security token issues with
    IFactory private products;

    address private distribution;

    event tradeSettled( 
                        bytes32 indexed poolid,
                        address transferor,
                        address transferee,
                        uint256 unitsToTransfer,
                        address security,
                        uint256 price,
                        address currency,
                        bytes32 settlementStatus,
                        uint256 fee
                    );

    event subscribers(bytes32 poolId, address party, address counterparty, 
                        address indexed securityTraded, address currencySettled, uint256 amount, uint256 price,
                        bytes32 tradeRef, bytes32 DPID, uint256 timestamp);

    modifier onlyIssueManager(address sender, address security){
        address DP = products.getDP(security);
        require(DP==sender, "Unauthorized access, only issue managers are authorized");
        _;
    }

    /**
        Initializes this asset management contract
        @param  _factory            reference to the secondary issue pool factory contract
        @param  _issueFeePercentage  percentage of trading fee to be charged by the asset manager
        @param  _client             address of Verified client contract
        @param  _vault              address of balancer vault
     */
    function initialize(address _factory, uint256 _issueFeePercentage, address _client, address _vault, address _distribution, address _products) onlyOwner public {
        factory = _factory;
        fee = _issueFeePercentage;
        client = VerifiedClient(_client);
        vault = _vault;
        products = IFactory(_products);
        distribution = _distribution;
    }

    /**
        Joins existing (secondary) tokenized assets into pool by primary issue token holders
        @param  security            reference to asset issued
        @param  currency            reference to cash (settlement) token
        @param  securityAmount      amount of security to issue
        @param  minSecurityOrder    minimum order size of securities issued in pool
        @param  cashAmount          amount of currency issued in pool
     */
    function issueSecondary(address security, address currency, uint256 securityAmount, uint256 minSecurityOrder, uint256 cashAmount) override onlyIssueManager(msg.sender, security) external {
        require(IERC20(security).balanceOf(msg.sender)>=securityAmount && securityAmount>=minSecurityOrder
                && IERC20(currency).balanceOf(msg.sender)>=cashAmount
                , "Insufficient balances to create secondary pool");
        ERC20(currency).transferFrom(msg.sender, address(this), cashAmount);
        ERC20(security).transferFrom(msg.sender, address(this), securityAmount);  
        ISecurity(security).whiteList(vault, securityAmount);      
        address pool = ISecondaryIssuePoolFactory(factory).create(
            ERC20(security).name(),
            ERC20(security).symbol(),
            security,
            currency,
            minSecurityOrder,
            fee
        ); 
        bytes32 poolId = ISecondaryIssuePool(pool).getPoolId();  
        poolSecurity[poolId] = security; 
        pools[pool] = poolId;
        // join the pool here                
        (IERC20[] memory tokens, , ) = IVault(vault).getPoolTokens(poolId);
        IAsset[] memory assets = _convertERC20sToAssets(tokens);
        uint256[] memory maxAmountsIn = new uint256[](tokens.length);
        ERC20(currency).approve(vault, cashAmount);
        ERC20(security).approve(vault, securityAmount);
        for (uint256 t; t < tokens.length; t++) {
            if(tokens[t]==IERC20(security))
                maxAmountsIn[t] = securityAmount;
            else if(tokens[t]==IERC20(currency))
                maxAmountsIn[t] = cashAmount;
        }
        IVault.JoinPoolRequest memory request = IVault.JoinPoolRequest({
            assets: assets,
            maxAmountsIn: maxAmountsIn,
            userData: abi.encode(JoinKind.INIT, maxAmountsIn),
            fromInternalBalance: false
        });         
        IVault(vault).joinPool(poolId, address(this), address(this), request);
    }

    /**
        Used by Secondary pools to request settlement for trades
        @param  tradeToReport     data structure holding order matching data
     */
    function requestSettlement(ITrade.trade memory tradeToReport, IOrder orderbook) override external {
        require(pools[msg.sender]!="", "Settlement requested from invalid pool");
        IOrder.order memory orderByParty = orderbook.getOrder(tradeToReport.partyRef);
        IOrder.order memory orderByCounterparty = orderbook.getOrder(tradeToReport.counterpartyRef);
        address securityTraded = orderbook.getSecurity(); 
        address currencySettled = orderbook.getCurrency();
        address party = orderByParty.tokenIn==securityTraded || orderByParty.tokenOut==currencySettled ? orderByParty.party : orderByCounterparty.party;
        address counterparty = orderByCounterparty.tokenIn==currencySettled || orderByCounterparty.tokenOut==securityTraded ? orderByCounterparty.party : orderByParty.party;
        uint256 amount = tradeToReport.securityTraded; 
        bytes32 _DPID = getTransferAgent(securityTraded);
        ISettlor.settlement memory tradeToSettle = ISettlor.settlement({
            transferor: party,
            transferee: counterparty,
            security: securityTraded,
            status: "Pending",
            currency: currencySettled,
            price: _downscaleDown(tradeToReport.price, _computeScalingFactor(IERC20(currencySettled))),
            unitsToTransfer: amount,
            consideration: _upscale(tradeToReport.currencyTraded, _computeScalingFactor(IERC20(currencySettled))),
            executionDate: tradeToReport.dt,
            partyRef: orderByParty.tokenIn==securityTraded || orderByParty.tokenOut==currencySettled ? tradeToReport.partyRef : tradeToReport.counterpartyRef,
            counterpartyRef: orderByCounterparty.tokenIn==currencySettled || orderByCounterparty.tokenOut==securityTraded ? tradeToReport.counterpartyRef : tradeToReport.partyRef,
            DPID: _DPID,
            orderPool: orderbook
        });
        bytes32 ref = keccak256(abi.encodePacked(tradeToReport.partyRef, tradeToReport.counterpartyRef));
        if(settlements[ref].transferor==address(0x0)){
            settlements[ref] = tradeToSettle;
            ISettlor.subscriptions memory subscription = ISettlor.subscriptions({
                investor: counterparty,
                asset: securityTraded,
                currency: currencySettled, 
                amount: amount,   
                price: _downscaleDown(tradeToReport.price, _computeScalingFactor(IERC20(currencySettled)))
            });
            bytes32 poolId = orderbook.getPoolId();
            investors[poolId].push(subscription);
            emit subscribers(poolId, party, counterparty, securityTraded, currencySettled, amount, subscription.price, ref, tradeToSettle.DPID, block.timestamp);
            trades[tradeToSettle.DPID].push(ref);
            if(products.getDP(securityTraded)==address(0x0))
                setSettlement(ref, "Confirm");
        }
    }

    /**
        Fetches trade settlement requests for depository participant
        @param  dpid         identifier of depository participant (eg, transfer agent) that is authorized to settle the asset 
     */
    function getSettlementRequests(bytes32 dpid, bytes32 poolId) override onlyIssueManager(msg.sender, poolSecurity[poolId]) external view returns(bytes32[] memory){
        /*if(poolId!=""){
            bytes32[] memory refs = new bytes32[](trades[dpid].length);
            uint j=0;
            for(uint256 i=0; i<trades[dpid].length; i++){
                 IOrder ob = settlements[trades[dpid][i]].orderPool;
                 bytes32 poolid = ob.getPoolId();
                 if(poolid==poolId){
                     refs[j] == trades[dpid][i];
                     j++;
                 }
            }
            return refs;
        }
        else{*/
            return trades[dpid];
        //}
    }

    /**
        Fetches a specific trade settlement request        
        @param  ref         reference to the trade
     */
    function getSettlementRequest(bytes32 ref) override external view returns(settlement memory){
        require(ref!="", "Settlement requests can only be fetched by a depositary participant"); 
        return settlements[ref];
    }

    /**
        Sets settlement status for trades executed
        @param  ref         reference to the trade executed
        @param  status      settlement status
     */
    function setSettlementStatus(bytes32 ref, bytes32 status) override onlyIssueManager(msg.sender, settlements[ref].security) external{
        setSettlement(ref, status);
    }

    function setSettlement(bytes32 ref, bytes32 status) private {
        bytes32 DPID = settlements[ref].DPID;
        bool settled = false;
        require(status=="Confirm" || status=="Reject", "Settlement status invalid");
        fee = FixedPoint.mulDown(settlements[ref].consideration, fee);
        uint256 pay = FixedPoint.divDown(fee, _computeScalingFactor(IERC20(settlements[ref].currency)));
        if(status=="Confirm" && settlements[ref].status!="Confirm" && settlements[ref].status!="Fail"){            
            fees[settlements[ref].currency] = Math.add(fees[settlements[ref].currency], pay);
            if(IERC20(settlements[ref].currency).transferFrom(settlements[ref].transferor, address(this), pay)){                
                settlements[ref].orderPool.orderFilled(settlements[ref].partyRef, settlements[ref].counterpartyRef, settlements[ref].executionDate);
                for(uint i=0; i<trades[DPID].length; i++){
                    if(trades[DPID][i]==ref){
                        if(i==trades[DPID].length-1)
                            delete trades[DPID][i];
                        else
                            trades[DPID][i] = trades[DPID][i+1];
                    }
                }
                settlements[ref].status = "Confirm";    
                settled = true;            
            }
            else{
                uint256 consideration = _downscaleDown(settlements[ref].consideration, _computeScalingFactor(IERC20(settlements[ref].currency)));
                settlements[ref].orderPool.revertTrade(settlements[ref].partyRef, settlements[ref].unitsToTransfer, IOrder.Order.Sell, settlements[ref].executionDate);
                settlements[ref].orderPool.revertTrade(settlements[ref].counterpartyRef, consideration, IOrder.Order.Buy, settlements[ref].executionDate);
                IERC20(settlements[ref].currency).transferFrom(settlements[ref].transferor, settlements[ref].transferee, consideration);
                IERC20(settlements[ref].security).transferFrom(settlements[ref].transferee, settlements[ref].transferor, settlements[ref].unitsToTransfer);
                settlements[ref].status = "Fail";
                settled = true;
            }
        }
        else if(status=="Reject" && settlements[ref].status!="Reject"){
            uint256 consideration = _downscaleDown(settlements[ref].consideration, _computeScalingFactor(IERC20(settlements[ref].currency)));
            IERC20(settlements[ref].currency).transferFrom(settlements[ref].transferor, settlements[ref].transferee, consideration);
            IERC20(settlements[ref].security).transferFrom(settlements[ref].transferee, settlements[ref].transferor, settlements[ref].unitsToTransfer);
            settlements[ref].status = "Reject";
            settled == true;
        }
        if(settled){
            bytes32 poolId = settlements[ref].orderPool.getPoolId();
            emit tradeSettled(
                            poolId,
                            settlements[ref].transferor, 
                            settlements[ref].transferee, 
                            settlements[ref].unitsToTransfer,
                            settlements[ref].security,
                            settlements[ref].price,
                            settlements[ref].currency,
                            settlements[ref].status,
                            pay
                        );  
        }          
    }

    /**
        Retrieve investors in secondary pools
        @param  poolId  pool identity
     */
    function getSubscribers(bytes32 poolId) override onlyIssueManager(msg.sender, poolSecurity[poolId]) external view returns(ISettlor.subscriptions[] memory){
        return investors[poolId];
    }

    /**
        Gets transfer agent for the security in the trade
        @param  security  address of security traded
     */
    function getTransferAgent(address security) private view returns(bytes32){
        address dp = products.getDP(security);
        return(client.getTransferAgent(dp));
    } 

    /**
        Fetch trading fee collected in Balancer vault
        @param  _token  address of token in which fee is collected
     */
    function getTradingFeeCollected(address _token) override external returns(uint256){
        require(msg.sender==distribution, "Unauthorized fee collection request");
        uint256 feeCollected = fees[_token];
        IERC20(_token).transfer(distribution, feeCollected);
        fees[_token] = Math.sub(fees[_token], feeCollected);
        return feeCollected;
    }   

    /**
     * @dev This helper function is a fast and cheap way to convert between IERC20[] and IAsset[] types
     */
    function _convertERC20sToAssets(IERC20[] memory tokens) internal pure returns (IAsset[] memory assets) {
        // solhint-disable-next-line no-inline-assembly
        assembly {
            assets := tokens
        }
    }

    /**
     * @dev Returns a scaling factor that, when multiplied to a token amount for `token`, normalizes its balance as if
     * it had 18 decimals.
     */
    function _computeScalingFactor(IERC20 token) internal view returns (uint256) {
        // Tokens that don't implement the `decimals` method are not supported.
        uint256 tokenDecimals = ERC20(address(token)).decimals();

        // Tokens with more than 18 decimals are not supported.
        uint256 decimalsDifference = Math.sub(18, tokenDecimals);
        return FixedPoint.ONE * 10**decimalsDifference;
    }

    /**
     * @dev Reverses the `scalingFactor` applied to `amount`, resulting in a smaller or equal value depending on
     * whether it needed scaling or not. The result is rounded down.
     */
    function _downscaleDown(uint256 amount, uint256 scalingFactor) internal pure returns (uint256) {
        return FixedPoint.divDown(amount, scalingFactor);
    }

    /**
     * @dev Applies `scalingFactor` to `amount`, resulting in a larger or equal value depending on whether it needed
     * scaling or not.
     */
    function _upscale(uint256 amount, uint256 scalingFactor) internal pure returns (uint256) {
        // Upscale rounding wouldn't necessarily always go in the same direction: in a swap for example the balance of
        // token in should be rounded up, and that of token out rounded down. This is the only place where we round in
        // the same direction for all amounts, as the impact of this rounding is expected to be minimal (and there's no
        // rounding error unless `_scalingFactor()` is overriden).
        return FixedPoint.mulDown(amount, scalingFactor);
    }

}

