// Registry of registered securities
// (c) Kallol Borah, 2021

pragma solidity 0.5.7;
pragma experimental ABIEncoderV2;

import "../interfaces/ISecuritiesRegistry.sol";
import "../interfaces/IPoolFactory.sol";
import "../interfaces/ViaFactory.sol";
import "../interfaces/VerifiedClient.sol";
import "../interfaces/VerifiedIssues.sol";
import "../utilities/StringUtils.sol";
import "abdk-libraries-solidity/ABDKMathQuad.sol";
import "@openzeppelin/upgrades/contracts/upgradeability/ProxyFactory.sol";
import "@openzeppelin/contracts/ownership/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract SecuritiesRegistry is Ownable, ProxyFactory, ISecuritiesRegistry{

    using ABDKMathQuad for bytes16;
    using stringutils for *;

    ViaFactory private via;

    address poolFactory;
    address preTradeContract;
    address tradeContract;
    address postTradeContract;
    address clientContract;

    //mapping security token to security details
    mapping(address => security) private securityTokens;

    //array of security tokens
    address[] private tokens;

    //mapping of isin to security token
    mapping(bytes32 => address) private securityToken;

    //mapping of security token to corporate action category to corporate actions in the category
    mapping(address => mapping(string => string[])) private corporateActions;

    //mapping company to credit score
    mapping(bytes32 => bytes32) private creditScore;

    //mapping users to their investments
    mapping(address => address[]) private investments;
    
    //event signalling creation of a security token
    event SecurityCreated(address indexed security, bytes32 company, bytes32 isin, bytes32 currency, address issuer, uint256 amount);
    //event signalling corporate action
    event CorporateActionCreated(address indexed security, bytes32 company, bytes32 isin, string category, string action);
    //event signalling credit score report
    event ReportCreditScore(bytes32 company, bytes32 isin, bytes32 score);

    constructor() public Ownable() {
    }

    function initialize(address _viaFactory, address _poolFactory, address _preTradeContract, address _tradeContract, address _postTradeContract, address _clientContract) public onlyOwner {
        via = ViaFactory(_viaFactory);
        poolFactory = _poolFactory;
        preTradeContract = _preTradeContract;
        tradeContract = _tradeContract;
        postTradeContract = _postTradeContract;
        clientContract = _clientContract;
    }

    function createSecurity(address _issuer, address _security, bytes32 _currency, bytes32 _company, bytes32 _isin, bytes32 _settlementBy, bytes32 _itype, uint256 _amount) external {
        require(msg.sender==preTradeContract || VerifiedIssues(msg.sender).getOwner()==owner());
        address cash = via.getTokenByNameType(_currency, "ViaCash");
        require(cash!=address(0x0));
        
        if(!isRegistered(_company, _currency, _isin)){
            bytes memory _payload = abi.encodeWithSignature("initialize(bytes32,bytes32,address,address,address,address,address)", _company, _isin, preTradeContract, tradeContract, postTradeContract, clientContract, owner());

            address _token = deployMinimal(_security, _payload);
            emit SecurityCreated(_token, _company, _isin, _currency, _issuer, _amount);

            securityTokens[_token] = security(_company, _currency, _isin, "none", ABDKMathQuad.fromUInt(0), _issuer, _itype);
            securityToken[_isin] = _token;
            tokens.push(_token);

            IPoolFactory(poolFactory).createPool(_token, cash, _currency, _settlementBy);
        }
    }

    function isRegistered(bytes32 _company, bytes32 _currency, bytes32 _isin) private view returns(bool){
        address stoken = securityToken[_isin];
        if( securityTokens[stoken].company==_company &&
                securityTokens[stoken].currency==_currency)
            return true;
        else
            return false;
    }

    function getToken(bytes32 _currency, bytes32 _company, bytes32 _isin) external view returns(address){
        address stoken = securityToken[_isin];
        if( securityTokens[stoken].company==_company &&
                securityTokens[stoken].currency==_currency)
            return stoken;
    }

    function getSecuritiesIssued() external view returns(address[] memory){
        return tokens;
    }

    function getSecuritiesInvested() external view returns(address[] memory){
        return (investments[msg.sender]);
    }

    function getSecurityDetails(address _securityToken) external view returns(security memory){
        return securityTokens[_securityToken];
    }

    function isIsinPresent(bytes32 _isin) external returns(bool){
        address stoken = securityToken[_isin];
        if( stoken != address(0x0))
            return true;
        else
            return false;
    }

    function registerCorporateAction(string calldata _category, string calldata _action, bytes32 _isin) external{
        //only company or a DP/registrar can report corporate action
        require(_isin!="" && bytes(_category).length!=0 && stringutils.equal(_action,""));
        (bytes32 role, ) = VerifiedClient(clientContract).getRole(msg.sender);
        address stoken = securityToken[_isin];
        if(stoken!=address(0x0)){
            if( securityTokens[stoken].isin==_isin && 
                (securityTokens[stoken].issuer==msg.sender || role=="DP")){
                corporateActions[stoken][_category].push(_action);
                emit CorporateActionCreated(stoken, 
                                    securityTokens[stoken].company, 
                                    securityTokens[stoken].isin, 
                                    _category, _action);
            }
        }
    }

    function registerCreditScore(bytes32 _company, bytes32 _isin, bytes32 _score) onlyOwner external {
        //only verified admin can report credit score
        require((_company!="" || _isin!="") && _score!="");
        if(_isin=="")
            creditScore[_company] = _score;
        else if (_isin!=""){
            address stoken = securityToken[_isin];
            securityTokens[stoken].creditScore = _score; 
        }
        emit ReportCreditScore(_company, _isin, _score);
    }

    function registerPrice(bytes32 _isin, bytes16 _price, address _transferor, address _transferee) external {
        //only post trade contract can report price
        require(msg.sender==postTradeContract);
        require(_isin!="" && ABDKMathQuad.cmp(_price, ABDKMathQuad.fromUInt(0))>0);
        address stoken = securityToken[_isin];
        if(stoken!=address(0x0)){
            securityTokens[stoken].price==_price;
            investments[_transferee].push(stoken);
            uint256 balance = IERC20(stoken).balanceOf(_transferor);
            if(balance>0)
                investments[_transferor].push(stoken);
        }
    }

    function getCorporateActions(bytes32 _isin, string calldata _category) external view returns(string[] memory){
        require(_isin!="" && bytes(_category).length!=0);
        address stoken = securityToken[_isin];
        if(stoken!=address(0x0))
            return corporateActions[stoken][_category];
    }

    function getCreditScore(bytes32 _isin, bytes32 _company) external view returns(bytes32){
        require(_company!="" || _isin!="");
        if(_isin=="")
            return (creditScore[_company]);
        else if (_isin!=""){
            address stoken = securityToken[_isin];
            return (securityTokens[stoken].creditScore); 
        }
    }

    function getPrice(bytes32 _isin) external view returns(bytes16){
        require(_isin!="");
        address stoken = securityToken[_isin];
        if(stoken!=address(0x0))
            return (securityTokens[stoken].price);
    }

}
