pragma solidity 0.5.7;

import "./Snapshot.sol";
import "./utilities/Pausable.sol";
import "./interfaces/ISecurity.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/ownership/Ownable.sol";

contract Security is ISecurity, Ownable, Pausable, Snapshot {

    string private _name;
    string private _symbol;

    //issuer of this security token or the assigned depositary participant
    address private _issueManager;

    //this security token's holders
    address[] private holders;

    //mapping security token holders to amounts frozen in account (0 means all frozen)
    mapping(address => uint256) private froze;

    //mapping whitelisted wallets to any spend cap on them (0 means no cap)
    mapping(address => uint256) private whitelisted;

    /**
     * @dev Emitted when an address is frozen.
     */
    event Freeze(address indexed enforcer, address indexed owner);

    /**
     * @dev Emitted when an address is unfrozen.
     */
    event Unfreeze(address indexed enforcer, address indexed owner);

    event Burn(address indexed owner, uint256 amount);

    event Mint(address indexed beneficiary, uint256 amount);

    modifier onlyIssueManager (address sender){
        require(sender == _issueManager, "Unauthorized request, only issuers are allowed");
        _;
    }

    modifier onlyWhiteListed (address sender, uint256 amount){
        //require(whitelisted[sender] >= amount || whitelisted[sender]==0 || sender==_issueManager, "Wallet sending request not whitelisted");
        _;
    }

    /**
     * @dev Sets the values for {name} and {symbol}.
     *
     * The default value of {decimals} is 18. To select a different value for
     * {decimals} you should overload it.
     *
     * All two of these values are immutable: they can only be set once during
     * construction.
     */
    function initialize(bytes32 name_, bytes32 symbol_, address _owner, address issueManager) public initializer {
        Ownable.initialize(_owner);
        _name = string(abi.encodePacked(name_));
        _symbol = string(abi.encodePacked(symbol_));
        _issueManager = issueManager;
    }

    /**
     * @dev Returns the name of the token.
     */
    function name() public view returns (string memory) {
        return _name;
    }

    /**
     * @dev Returns the symbol of the token, usually a shorter version of the
     * name.
     */
    function symbol() public view returns (string memory) {
        return _symbol;
    }

    /**
     * Securities are indivisible, so returning decimal 0
     */
    function decimals() public view returns (uint8) {
        return 18;
    }

    function mintToken(address issuer, uint256 amount) onlyOwner external{
        //_beforeTokenTransfer(address(0x0), issuer, amount);
        _mint(issuer, amount);
        emit Mint(issuer, amount);
    }

    function transferToken(address transferor, address transferee, uint256 amount) onlyOwner external{
        //_beforeTokenTransfer(transferor, transferee, amount);
        _transfer(transferor, transferee, amount);
    }

    function whiteList(address spender, uint256 amount) external {
        whitelisted[spender] = amount;
    }

    /*function transfer(address recipient, uint256 amount) onlyWhiteListed(_msgSender(), amount) public returns (bool) {
        require(froze[recipient]==0, "Funds transfer frozen");
        _beforeTokenTransfer(_msgSender(), recipient, amount);
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function approve(address spender, uint256 amount) onlyWhiteListed(_msgSender(), amount) public returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) onlyWhiteListed(_msgSender(), amount) public returns (bool) {
        require(balanceOf(sender).sub(amount) > froze[sender], "Funds transfer frozen");
        _beforeTokenTransfer(sender, recipient, amount);
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), allowance(sender, _msgSender()).sub(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
        _approve(_msgSender(), spender, allowance(spender, _msgSender()).add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
        _approve(_msgSender(), spender, allowance(spender, _msgSender()).sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }*/

    function freeze(address holder, uint256 amount) onlyIssueManager(_msgSender()) public {
        froze[holder] = amount;
        emit Freeze(_msgSender(), holder);
    }

    function unfreeze(address holder) onlyIssueManager(_msgSender()) public {
        froze[holder] = 0;
        emit Unfreeze(_msgSender(), holder);
    }

    function frozen(address account) public view returns (uint256) {
        return froze[account];
    }

    function burn(address holder, uint256 amount) onlyIssueManager(_msgSender()) public {
        _beforeTokenTransfer(holder, address(0x0), amount);
        _burn(holder, amount);       
        emit Burn(holder, amount); 
    }

    function burnAll() onlyIssueManager(_msgSender()) public {
        for(uint256 i=0; i<holders.length; i++){
            _beforeTokenTransfer(holders[i], address(0x0), balanceOf(holders[i]));
            _burn(holders[i], balanceOf(holders[i]));
        }    
    }

    function scheduleSnapshot(uint256 time) public {
        _scheduleSnapshot(time);
    }

    function rescheduleSnapshot(uint256 oldTime, uint256 newTime) public {
        _rescheduleSnapshot(oldTime, newTime);
    }

    function unscheduleSnapshot(uint256 time) public {
        _unscheduleSnapshot(time);
    }

    function pause() onlyIssueManager(_msgSender()) public {
        _pause();
    }

    function unpause() onlyIssueManager(_msgSender()) public {
        _unpause();
    }

}
