// Verified securities manager that updates holdings of securities by users
// (c) Kallol Borah, 2021

pragma solidity 0.5.7;

import "./CorporateActions.sol";
import "./utilities/Pausable.sol";
import "./interfaces/IFactory.sol";
import "./interfaces/ISecurity.sol";
import "./interfaces/VerifiedClient.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/ownership/Ownable.sol";

contract Security is ISecurity, Ownable, Pausable, CorporateActions {

    using SafeMath for uint256;

    string private _name;
    string private _symbol;

    uint256 private MAX_NAME_LENGTH=32;
    uint256 private MAX_SYMBOL_LENGTH=32;

    VerifiedClient private _client;

    //issuer of this security token or the assigned depositary participant
    address private _issuer;

    //this security token's holders
    address[] private holders;

    //mapping holders to index
    mapping(address => uint256) private holderIndex;

    //mapping security token holders to amounts frozen in account (0 means all frozen)
    mapping(address => uint256) private froze;

    //mapping whitelisted wallets to any spend cap on them (0 means no cap)
    mapping(address => uint256) private whitelisted;

    /**
     * @dev Emitted when an address is frozen.
     */
    event Freeze(address indexed enforcer, address indexed owner);

    /**
     * @dev Emitted when an address is unfrozen.
     */
    event Unfreeze(address indexed enforcer, address indexed owner);

    event Burn(address indexed owner, uint256 amount);

    event Mint(address indexed beneficiary, uint256 amount);

    modifier onlyIssueManagerOrCustodian(address sender){
        (bytes32 role, , ) = _client.getRole(sender);
        require(IFactory(owner()).getDP(address(this))==sender || role=="Custodian", 
                "Not issuer or custodian");
        _;
    }

    modifier onlyIssueManager (address sender){
        require(IFactory(owner()).getDP(address(this))==sender, "Only issuers allowed");
        _;
    }

    modifier onlyWhiteListed (address sender, uint256 amount){
        require(whitelisted[sender] >= amount || IFactory(owner()).getDP(address(this))==sender || sender==_issuer, "Sender wallet not whitelisted");
        _;
    }

    /**
     * @dev Sets the values for {name} and {symbol}.
     *
     * The default value of {decimals} is 18. To select a different value for
     * {decimals} you should overload it.
     *
     * All two of these values are immutable: they can only be set once during
     * construction.
     */
    function initialize(bytes32 name_, bytes32 symbol_, address _owner, address issuer, address client) public initializer {        
        Ownable.initialize(_owner);
        _name = string(abi.encodePacked(name_));
        _symbol = string(abi.encodePacked(symbol_));
        //require(bytes(_name).length <= MAX_NAME_LENGTH, "Name too long");
        //require(bytes(_symbol).length <= MAX_SYMBOL_LENGTH, "Symbol too long");
        _issuer = issuer;
        _client = VerifiedClient(client);
    }

    /**
     * @dev Returns the name of the token.
     */
    function name() public view returns (string memory) {
        return _name;
    }

    /**
     * @dev Returns the symbol of the token, usually a shorter version of the
     * name.
     */
    function symbol() public view returns (string memory) {
        return _symbol;
    }

    /**
     * Securities are indivisible, so returning decimal 0
     */
    function decimals() public view returns (uint8) {
        return 18;
    }

    function mintToken(address issuer, uint256 amount) external onlyOwner{
        _beforeTokenTransfer(address(0x0), issuer, amount);
        _mint(issuer, amount);
        holders.push(issuer);
        holderIndex[issuer]=holders.length;
        emit Mint(issuer, amount);
    }

    function transferToken(address transferor, address transferee, uint256 amount) external onlyOwner{
        _beforeTokenTransfer(transferor, transferee, amount);
        _transfer(transferor, transferee, amount);
        if(holderIndex[transferee]==0){
            holders.push(transferee);
            holderIndex[transferee]=holders.length;
        }
    }

    function whiteList(address spender, uint256 amount) onlyWhiteListed(_msgSender(), amount) external {
        require(paused == false, "Contract paused");
        whitelisted[spender] = amount;
    }

    //can be used by primary and secondary issue manager contracts. These whitelisted contracts can also be frozen by the issuer.
    function transfer(address recipient, uint256 amount) onlyWhiteListed(_msgSender(), amount) public returns (bool) {
        require(paused == false, "Contract paused");
        require(balanceOf(_msgSender()).sub(amount) >= froze[_msgSender()], "Funds transfer frozen");
        _beforeTokenTransfer(_msgSender(), recipient, amount);
        _transfer(_msgSender(), recipient, amount);
        if(holderIndex[recipient]==0){
            holders.push(recipient);
            holderIndex[recipient]=holders.length;
        }
        return true;
    }

    //called by user wallets to approve vaults to transfer tokens from them to the vault. 
    //Vaults need to be whitelisted by primary and secondary issue managers.
    function approve(address spender, uint256 amount) onlyWhiteListed(spender, amount) public returns (bool) {
        require(paused == false, "Contract paused");
        _approve(_msgSender(), spender, amount);
        return true;
    }

    //can be used by primary and secondary issue manager contracts, as well as vaults to swap tokens from user wallets that have approved the vault.
    function transferFrom(address sender, address recipient, uint256 amount) public onlyWhiteListed(_msgSender(), amount) returns (bool) {
        require(paused == false, "Contract paused");
        require(balanceOf(sender).sub(amount) >= froze[sender], "Funds transfer frozen");
        _beforeTokenTransfer(sender, recipient, amount);
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), allowance(sender, _msgSender()).sub(amount, "ERC20: transfer amount exceeds allowance"));
        if(holderIndex[recipient]==0){
            holders.push(recipient);
            holderIndex[recipient]=holders.length;
        }
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
        require(paused == false, "Contract paused");
        _approve(_msgSender(), spender, allowance(spender, _msgSender()).add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
        require(paused == false, "Contract paused");
        _approve(_msgSender(), spender, allowance(spender, _msgSender()).sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }

    function freeze(address holder, uint256 amount) public onlyIssueManager(_msgSender()) {
        require(paused == false, "Contract paused");
        froze[holder] = amount;
        emit Freeze(_msgSender(), holder);
    }

    function unfreeze(address holder) public onlyIssueManager(_msgSender()) {
        require(paused == false, "Contract paused");
        froze[holder] = 0;
        emit Unfreeze(_msgSender(), holder);
    }

    function frozen(address account) public view returns (uint256) {
        require(paused == false, "Contract paused");
        return froze[account];
    }

    function burn(address holder, uint256 amount) public onlyIssueManager(_msgSender()) {
        require(paused == false, "Contract paused");
        _beforeTokenTransfer(holder, address(0x0), amount);
        _burn(holder, amount);       
        emit Burn(holder, amount); 
    }

    function burnAll() public onlyIssueManager(_msgSender()) {
        require(paused == false, "Contract paused");
        for(uint256 i=0; i<holders.length; i++){
            _beforeTokenTransfer(holders[i], address(0x0), balanceOf(holders[i]));
            _burn(holders[i], balanceOf(holders[i]));
        }    
    }

    function scheduleSnapshot(uint256 time) public onlyIssueManager(_msgSender()) {
        require(paused == false, "Contract paused");
        _scheduleSnapshot(time);
    }

    function rescheduleSnapshot(uint256 oldTime, uint256 newTime) public onlyIssueManager(_msgSender()) {
        require(paused == false, "Contract paused");
        _rescheduleSnapshot(oldTime, newTime);
    }

    function unscheduleSnapshot(uint256 time) public onlyIssueManager(_msgSender()) {
        require(paused == false, "Contract paused");
        _unscheduleSnapshot(time);
    }

    function createResolution(uint256 time, bool votes, string memory ipfsLink) public onlyIssueManager(_msgSender()) {
        require(paused == false, "Contract paused");
        _createResolution(time, votes, ipfsLink);
    }
    
    function countVotes(uint256 time) public view onlyIssueManager(_msgSender()) returns(uint256 total, uint256 yes){
        require(paused == false, "Contract paused");
        uint256 voteCount = 0;
        uint256 totalCount = 0;
        if(keccak256(abi.encodePacked(resolutions[time])) != keccak256(abi.encodePacked(""))){
            if(voting[resolutions[time]]==true){                
                for(uint256 i=0; i<holders.length; i++){
                    if(snapshotBalanceOf(time, holders[i])>0){
                        totalCount = totalCount + 1;
                        if(votes[time][holders[i]]==true)
                            voteCount = voteCount + 1;
                    }
                }
            }
        }
        return (totalCount, voteCount);       
    } 

    function payoutProrata(uint256 time, address wallet, address token, uint256 amount) public onlyIssueManagerOrCustodian(_msgSender()) {
        require(paused == false, "Contract paused");
        require(snapshotTotalSupply(time)>0, "No scheduled snapshot found");
        require(ERC20(token).balanceOf(wallet) >= amount, "Insufficient balance");
        require(token!=address(0x0) && wallet!=address(0x0) && amount>0, "Invalid distribution");
        uint256 totalHoldings = snapshotTotalSupply(time);
        for(uint256 i=0; i<holders.length; i++){
            uint256 holding = snapshotBalanceOf(time, holders[i]);
            if(holding > 0){
                uint256 prorata = SafeMath.mul(SafeMath.div(holding, totalHoldings), amount);
                ERC20(token).transferFrom(wallet, holders[i], prorata);
                distributions[time][holders[i]].token = token;
                distributions[time][holders[i]].amount = prorata;
            }
        }
    }

    function payout(uint256 time, address holder, address wallet, address token, uint256 amount) public onlyIssueManagerOrCustodian(_msgSender()) {
        require(paused == false, "Contract paused");
        require(snapshotTotalSupply(time)>0, "No scheduled snapshot found");
        require(ERC20(token).balanceOf(wallet) >= amount, "Insufficient balance");
        require(snapshotBalanceOf(time, holder)>0, "Not a security token holder");
        require(token!=address(0x0) && wallet!=address(0x0) && amount>0, "Invalid distribution");
        ERC20(token).transferFrom(wallet, holder, amount);
        distributions[time][holder].token = token;
        distributions[time][holder].amount = amount;
    }

    function pause() public onlyIssueManager(_msgSender()){
        _pause();
    }

    function unpause() public onlyIssueManager(_msgSender()){
        _unpause();
    }

}
